INSERT INTO `restaurant` (`restaurant_Id`, `Name`, `Description`, `Addr`, `Zipcode`, `Region`, `Town`, `Tel`, `Opentime`, `Website`, `Picture1`, `Picdescribe1`, `Picture2`, `Picdescribe2`, `Picture3`, `Picdescribe3`, `Px`, `Py`, `Class`, `Map`, `Parkinginfo`) VALUES
('C3_315080000H_000009', '龍潭豆花', '龍潭豆花只賣一種東西，也就是現點現做的古早味豆花，讓饕客把味蕾的重點回歸到豆花本身，豆花滑順綿密，比例調配得很好，帶著微微焦香，豆花出自店家自製，有著自己的特色。', '臺北市中正區汀州路三段239號', 0, '臺北市', '中正區', '', '無', '', '', '', '', '', '', '', '121.534100', '25.013920', 5, '', 'N/A'),
('C3_315080500H_000013', '望海巴耐野菜餐廳', '非常有特色的原住民餐點餐廳，位於台十一線8K區段上，是東海岸行經花蓮大橋進入東海岸國家風景區之後，花蓮遊客中心前，第一家餐飲服務業者；業者於建物外部以當地竹子搭蓋起大門及挑高竹亭，呈顯其自然風格建築形式是其特色。望海巴耐野菜餐廳位於台十一線8K區段上，是東海岸行經花蓮大橋進入東海岸國家風景區之後，花蓮遊客中心前，第一家餐飲服務業者；業者於建物外部以當地竹子搭蓋起大門及挑高竹亭，呈顯其自然風格建築形式是其特色。', '花蓮縣974壽豐鄉鹽寮村大橋22號', 974, '花蓮縣', '壽豐鄉', '886-9-37533483', '11:30 - 20:00', '', '', '', '', '', '', '', '121.606110', '23.918950', 9, '', ''),
('C3_315080500H_000016', '噶瑪蘭海產店', '沒有固定菜單，菜單依照時節隨時更新，主要客群來自附近居民及固定客戶，業者也和附近民宿配合，接待海天、118、沙漠風情、來去海邊等民宿的住房遊客。主要菜色為海鮮料理及噶瑪蘭風味料理，餐廳本身還有供簡易之餐飲服務，可容納約90~120人，這是一間原住民風格濃厚的餐廳。', '花蓮縣977豐濱鄉新社村42號', 977, '花蓮縣', '豐濱鄉', '886-3-8711339', '11:00 - 14:00、17:00 - 19:00', '', '', '', '', '', '', '', '121.537790', '23.653180', 9, '', ''),
('C3_315080500H_000017', '昕陽餐廳', '以海鮮熱炒為主要菜單，主要客源來自當地及附近鄉鎮居民，旺季時常有團體客群用餐，餐廳可容納120人以上，位處石梯港旁，用餐環境良好，在石梯漁港附近有好幾間海鮮餐廳，是遊客到東海岸品嘗海鮮的重要據點，其中以老字號「昕陽餐廳」一魚五吃的料理最為知名，餐廳的魚蝦都是老板趁著清晨漁船靠岸時，港邊挑選的新鮮魚貨，這間餐廳是石梯港的老字號。', '花蓮縣977豐濱鄉石梯灣港邊96-2號', 977, '花蓮縣', '豐濱鄉', '886-3-8781396', '11:00 - 19:30', '', '', '', '', '', '', '', '121.505900', '23.492170', 9, '', ''),
('C3_315080500H_000018', '口福海鮮餐廳', '位處石梯港邊，臨近昕陽餐廳，同時也是東部賞鯨一號的報名處，餐廳主要提供海鮮熱炒料理，室內空間可容納200人以上，淡季客源除了過路觀光客，主要接待當地及附近居民，旺季則以接待遊覽車團體客人為主，是吃飯加賞鯨的好所在。', '花蓮縣977豐濱鄉石梯港82號', 977, '花蓮縣', '豐濱鄉', '886-3-8781041', '11:00 - 14:00 、 17:00 - 19:30', '', '', '', '', '', '', '', '121.510850', '23.488160', 9, '', ''),
('C3_315080500H_000019', '拙而奇藝術空間', '結合藝術雕塑與簡單飲食的地方，以提供咖啡簡餐為主，是一個以創意藝術工作展示空間為主的餐廳，藝術濃厚的用餐空間，展示的作品為東海岸之鵝卵石及漂流木結合而成的藝術創作，只有假日才供餐，平日只提供飲料，參觀不需門票，但有最低消費五十元之限制，是一個非常有特色的空間。', '花蓮縣977豐濱鄉港口村石門3號', 977, '花蓮縣', '豐濱鄉', '886-3-8781616', '平日13:00~17:00為下午茶時段 (不供應簡餐)暑期7月～8月上午11:00可事先預約消費遇春節及連續假日三天以上11:00~17:00歡迎來電詢問', '', '', '', '', '', '', '', '121.502640', '23.522370', 9, '', ''),
('C3_315080500H_000020', '東興野菜牛肉麵', '東興牛肉麵位於新社村台11線道上，因為產品的獨特性，附近同業大多經營海鮮餐廳，不相衝突，因此生意很不錯，來往遊客也多會停留。餐廳內部約可容納約50人。', '花蓮縣977豐濱鄉新社村9鄰東興6-1號', 977, '花蓮縣', '豐濱鄉', '886-3-8711282', '10:00 - 19:00', '', '', '', '', '', '', '', '121.530900', '23.634220', 9, '', ''),
('C3_315080500H_000021', '阿卿海產店', '深受當地人喜愛的海產店，阿卿海鮮位於豐濱村內，主要提供海鮮料理，因為地處豐濱最熱鬧的地區，菜單也多元可選擇，除了觀光客經常停留之外，也是當地居民喜愛前往聚餐的地方。', '花蓮縣977豐濱鄉豐濱村三民路33號', 977, '花蓮縣', '豐濱鄉', '886-3-8791287', '11:00 - 14:00、17:00 - 1900', '', '', '', '', '', '', '', '121.521460', '23.598300', 9, '', ''),
('C3_315080500H_000026', '升火工作室', '業者在工作室開設咖啡廳，也在工作室門口經營煙燻飛魚的小販，原本有經營民宿的林鳳美小姐，同時也是港口部落觀光發展協會的總幹事，原本也在工作室經營民宿的他，目前也已經歇業，只剩咖啡廳在經營。', '花蓮縣977豐濱鄉港口村石梯灣117-2號', 977, '花蓮縣', '豐濱鄉', '886-9-78502180', '若想參觀請事先打電話預約', '', '', '', '', '', '', '', '121.508830', '23.489180', 9, '', ''),
('C3_315080500H_000031', '明來餐廳', '在八仙洞對面的餐廳，30年的老店，店內除了供應海鮮料理、合菜外，另外還有販售池上便當、台糖冰品、青草茶等飲料冰品，本餐廳也有附素食，餐廳外牆用向日葵圖案裝飾，從餐廳望出去就是遼闊的大海，可以享受美食又同時欣賞海景。', '臺東縣962長濱鄉三間村水母丁6-2號', 962, '臺東縣', '長濱鄉', '886-8-9881136', '10:00~15:00', '', '', '', '', '', '', '', '121.477930', '23.396990', 9, '', ''),
('C3_315080500H_000032', '濱海餐廳', '濱海餐廳是臺東縣以台菜為主的美食餐廳，供應高級平時吃不到的山產美食、海產美食。', '臺東縣962長濱鄉三間村水母丁1-9號', 962, '臺東縣', '長濱鄉', '886-8-9881318', '11:00 – 18:00', '', '', '', '', '', '', '', '121.476470', '23.393650', 9, '', ''),
('C3_315080500H_000035', '烏石鼻海鮮餐廳', '烏石鼻活海產餐廳是一個廣受老饕喜愛的餐廳。距離餐廳東南側200公尺就是烏石鼻天然漁港，此地的海產都是當地漁民入夜時出海捕撈，於清晨販賣給店家，食材都非常新鮮。　在店裡販售的魚類店家老闆都可以做成新鮮味美的生魚片，隨著種類不同口味也不同，所有種類的生魚片湊合起來可以成為一桌味覺饗宴。品嚐海鮮時，同時欣賞浪濤一波波撲向沙灘，真是視覺和味覺的享受。', '臺東縣962長濱鄉寧埔村烏石鼻42-2號', 962, '臺東縣', '長濱鄉', '886-8-9801619', '11:30~14:00，17:00~19:30', '', '', '', '', '', '', '', '121.414270', '23.230160', 9, '', ''),
('C3_315080500H_000036', '欣逢海鮮餐廳', '位於台11線旁的海鮮餐廳，提供經濟海鮮、團體合菜。', '臺東縣962長濱鄉寧埔村17鄰界橋15號', 962, '臺東縣', '長濱鄉', '886-8-9801180', '10:30–19:00', '', '', '', '', '', '', '', '121.409440', '23.216630', 9, '', ''),
('C3_315080500H_000046', '晨霄海鮮', '看似簡陋的小餐廳，裡面卻提供著平價好吃的海鮮料理，旅行途中在台11線上奔馳疲累時，不妨停下腳步來到晨霄小吃部品嘗好吃的海鮮料理。', '臺東縣962長濱鄉寧埔村烏石鼻14鄰41-3號', 962, '臺東縣', '長濱鄉', '886-8-9801368', '11:00~14:00、17:00~19:30', '', '', '', '', '', '', '', '121.414100', '23.227330', 9, '', ''),
('C3_315080500H_000048', '原鄉複合式美食館', '融合12族文化的原住民風格餐廳，親切的服務態度，精緻美味的特色餐飲，五六日更推出199吃到飽專案，是您假日絕不能錯過的美食餐廳。2樓有30多個座位 歡迎包下整層。星期六只有晚上營業 因為老闆作禮拜；過年時整天營業。', '臺東縣961成功鎮大同路111-1號', 961, '臺東縣', '成功鎮', '886-8-9853328', '11:00~14:00，17:30~22:00', '', '', '', '', '', '', '', '121.378690', '23.104240', 9, '', ''),
('C3_315080500H_000050', '皇家山東牛肉麵', '精選上等牛肉和20種中藥材文火料理6~8小時的香醇美味，不加味素.湯頭不油不膩.清爽口感，是愛吃牛肉麵的老饕必吃絕品店!本店有麵.飯.餃.湯和小菜等，特別推廌飛魚卵.皮蛋豆腐與旗魚生魚片小菜(旗魚生魚片因配合漁會時間，只供應到18:00 pm)', '臺東縣961成功鎮三民里大同路43號', 961, '臺東縣', '成功鎮', '886-8-9853845', '10:30~20:00', '', '', '', '', '', '', '', '121.375180', '23.102680', 9, '', ''),
('C3_315080500H_000052', '甯記東河包子店', '以實在的價格.認真親切的精神服務顧客，讓消費者在品嘗甯記東河包子後能有溫暖.家的感覺。', '臺東縣959東河鄉東河村10鄰266號1樓', 959, '臺東縣', '東河鄉', '886-8-9896737', '05:30 - 18:30，週三店休', '', '', '', '', '', '', '', '121.275620', '22.973030', 9, '', ''),
('C3_315080500H_000053', '東河肉粽王', '實在美味飽足的好吃肉粽!搭配店內豬腸熬煮3小時的濃郁四神湯與滷肉飯，保證讓你吃得很過癮!四神湯.肉粽', '臺東縣959東河鄉南東河64號', 959, '臺東縣', '東河鄉', '886-8-9896665', '9:00~20:00', '', '', '', '', '', '', '', '121.301280', '22.966130', 9, '', ''),
('C3_315080500H_000055', '新東糖廠-煙囪下小吃', '如果您在旅途中累了，回想起家鄉，那你將會在煙囪下找到熟悉的家鄉味道，熱情的老闆娘煮得一手道地的家鄉味，絕對讓你回味不已。', '臺東縣959東河鄉都蘭村69號(糖廠內)', 959, '臺東縣', '東河鄉', '886-9-11733010', '10:00~18:30', '', '', '', '', '', '', '', '121.226340', '22.872170', 9, '', ''),
('C3_315080500H_000059', '馬利諾廚房', '用心實在的手工堅持，不添加任何膨鬆劑.發酵劑，讓大朋友小朋友都可以安心品嘗.由義大利美國人專門製作的營養麵包，限量供應，陸續推出健康美味的新產品，歡迎來店預購!週六日還有提供義大利麵食哦。超級麵包Super Bread.肉桂葡萄麵包---真的很特別.營養.好吃!', '臺東縣959東河鄉都蘭村436-3號', 959, '臺東縣', '東河鄉', '886-8-9531848', '09:30-18:30', '', '', '', '', '', '', '', '121.230830', '22.878640', 9, '', ''),
('C3_315080500H_000145', '美娥海產店', '&quot;美娥&quot;----老闆娘林美娥的本名，約在十七年前老闆娘因面臨傳統產業轉型機緣之下接觸了餐飲業(小吃店)，由於她那親切的待客之道、敬業精神及對品質的要求，才有今日的美娥海產。美娥老闆娘很珍惜現有的一切，因此，更加用心的經營，將傳統美食與精緻料理相結合，以優值的服務呈現給愛護她的客戶。', '臺東縣950臺東市富岡里吉林路二段669號', 950, '臺東縣', '臺東市', '886-8-9281062', '11:00~14:30，17:00~20:00 ( 每週二、週三公休 )', '', '', '', '', '', '', '', '121.185140', '22.791370', 9, '', ''),
('C3_315080500H_000146', '藍色愛情海餐廳', '由台東市區一個藍色小通道才能到達的風味餐店，熟知門路的內行人常來這裡看海景.喝飲料.吃海鮮山產.聽素人音樂唱歌，台東市遊玩絕不能錯過的好地點!', '臺東縣950臺東市富岡里吉林路二段638巷4號', 950, '臺東縣', '臺東市', '886-8-9281758', '09:00 - 23:00', '', '', '', '', '', '', '', '121.185040', '22.791080', 9, '', ''),
('C3_315080500H_000148', '龍港海鮮餐廳', '乾淨舒適，用心親切，料好實在的一間海鮮料理店，歡迎遊覽車客團餐，三五好友聚餐，為您提供各種海鮮合菜美食。', '臺東縣950臺東市富岡里松江路一段178號', 950, '臺東縣', '臺東市', '886-8-9281756', '09:00~14:00，16:30~20:00', '', '', '', '', '', '', '', '121.189150', '22.793840', 9, '', ''),
('C3_315080500H_000165', '釣魚人餐廳', '因為熱愛海釣，讓詹老闆戀上了綠島。名家藝術桌椅、締造紀錄的魚骨標本，展現濃厚的個人風格。海堤邊用餐、露天巴比Q，多了豁達和率性。而擺上的新鮮魚貨是口碑，也是招牌，這樣才不負「釣魚人」的名號。', '臺東縣951綠島鄉南寮村111號', 951, '臺東縣', '綠島鄉', '886-8-9671022', '無', '', '', '', '', '', '', '', '121.468830', '22.668670', 9, '', ''),
('C3_315080500H_000182', '美玲平價海鮮', '本店為國民旅遊卡特約店，提供經濟套餐、各式海鮮合菜，應有盡有，漁貨新鮮，價格平實，喜愛海鮮的人來到成功鎮絕對不可錯過。', '臺東縣961成功鎮中山東路85號', 961, '臺東縣', '成功鎮', '886-8-9851638', '11:00 - 14:30 、 17:00 - 19:30', '', '', '', '', '', '', '', '121.380350', '23.101110', 9, '', ''),
('C3_315080500H_000184', '日昇餐廳', '日昇餐館位在成功鎮中山路，以本地的漁獲海鮮為主要素材，並提供新鮮衛生的海產、簡餐、和菜和外帶便當等能迎合各種客人的餐飲需求，店內上至總鋪師下至餐飲服務都是自家人，為的是能同心提供優質的餐飲服務。', '臺東縣961成功鎮中山路81號', 961, '臺東縣', '成功鎮', '886-8-9855133', '09:00~21:00', '', '', '', '', '', '', '', '121.379080', '23.104350', 9, '', ''),
('C3_315080500H_000186', '雲海海產店', '本餐廳曾於2007年獲得台東縣政府推動經濟部商業司小鎮商業圈計畫，結合成功鎮公所辦理的「旗魚特色餐料理競賽」金廚獎。', '臺東縣961成功鎮和平路34-9號', 961, '臺東縣', '成功鎮', '886-8-9841551', '10:00–14:00;17:00–20:00', '', '', '', '', '', '', '', '121.344400', '23.053480', 9, '', ''),
('C3_315080500H_000190', '新發(宋媽媽)海產店', '一進入新發宋媽媽海產店，你會發現餐廳內滿滿的簽名留言，連天花板、冷氣、樓梯口都有，老闆娘宋媽媽待人親切和藹熱情，本店原來店名為宋媽媽海產，因擴大營業，才將店名改為「新發海產店」，但老顧客都還是以宋媽媽海產稱呼。', '臺東縣961成功鎮中華路193號', 961, '臺東縣', '成功鎮', '886-8-9851671', '11:00 - 14:00 、 17:00 - 20:00', '', '', '', '', '', '', '', '121.381910', '23.101720', 9, '', ''),
('C3_315080500H_000192', '新澎湖海鮮餐廳', '新澎湖海鮮餐廳位於成功鎮市區，提供團體合菜外另有喜宴外燴服務，亦歡迎散客光臨。餐廳王老闆家中同時也是東海岸「台11白蝦」的養殖業者，因此餐廳除了有自家養殖的新鮮蝦料理外，海鮮類食材也來自成功本地漁港的新鮮魚貨。餐廳周邊地坪廣大，停車方便，加上菜色多元豐富，吸引不少饕客聞香而至。', '臺東縣961成功鎮中山路167號', 961, '臺東縣', '成功鎮', '886-8-9852780', '11:00 - 14:00 、 17:00 - 20:00', '', '', '', '', '', '', '', '121.381610', '23.106990', 9, '', ''),
('C3_315080500H_000196', '群芳樓海產村', '本餐廳沒有華麗的店面，但乾淨舒適的用餐環境是本店的堅持，老闆對於每道菜都用心講究 ， 本店曾參加2007年至高雄夢時代廣場比賽，來到成功鎮一定要吃海鮮才代表你有來過。', '臺東縣961成功鎮新生路93-1號', 961, '臺東縣', '成功鎮', '886-8-9850039', '11:00 - 21:00', '', '', '', '', '', '', '', '121.380150', '23.103820', 9, '', ''),
('C3_315080500H_000206', '竹屋美食城', '12年前， 老闆夫婦離開台東市，回到妻子的故鄉開了這家店。除了一般迎合大眾口味的菜式之外，以當地特有食材烹調的漁家私房菜也是一大賣點。道地的風味，不妨前來一試。', '臺東縣951綠島鄉南寮村200-1號', 951, '臺東縣', '綠島鄉', '886-8-9672129', '10:30-14:30，17:00-20:30(夏季)', '', '', '', '', '', '', '', '121.468680', '22.668510', 9, '', ''),
('C3_315080500H_000208', '池塘有魚', '舒適的用餐環境，展現不凡品味，不僅桌椅皆為實心柚木，食材也是真材實料。熱騰騰上桌的菜餚都是現做、現炸，傳達了主人的溫馨。', '臺東縣951綠島鄉南寮村137號', 951, '臺東縣', '綠島鄉', '886-8-9672683', '10:00~14:00;17:00~22:00', '', '', '', '', '', '', '', '121.469550', '22.667120', 9, '', ''),
('C3_315080500H_000210', '翔記餐廳', '翔記老闆林義森來綠島經營已有16年之久，從處理鹿肉開始做起，人稱「鹿肉林」，對研發菜餚頗為在行，具備外燴辦桌的本領。手工拉麵是一大特色，不添加色素與防腐劑， 客人可以看到手工製麵的過程。', '臺東縣951綠島鄉南寮村191-1號', 951, '臺東縣', '綠島鄉', '886-8-9672021', '無', '', '', '', '', '', '', '', '121.471590', '22.664820', 9, '', ''),
('C3_315080500H_000212', '東昇溫泉羊肉爐', '白天不營業的時候，跟一般民宅沒兩樣，實在看不出這家羊肉爐已賣了1 0 多年， 還曾有不少名人來光顧。但一到傍晚，饕客陸續報到，屋前廣場擺起了桌椅。主人忙不過來，客人還會自動自發張羅碗盤，相當有意思。', '臺東縣951綠島鄉公館村溫泉42號', 951, '臺東縣', '綠島鄉', '886-8-9672067', '18:00-22:00', '', '', '', '', '', '', '', '121.498540', '22.646410', 9, '', ''),
('C3_315080500H_000218', '文鳳小吃', '因為丈夫天天出海捕魚，所以文鳳小吃總有最新鮮的魚貨，加上漁船自己的，價格相當實惠。而看似簡單的料理，準備功夫並不馬虎，為求最佳口感，食材皆細心處理過才下鍋，是地方頗為推崇的小店。', '臺東縣951綠島鄉南寮村漁港路37號', 951, '臺東縣', '綠島鄉', '886-8-9672003', '無', '', '', '', '', '', '', '', '121.473260', '22.662930', 9, '', ''),
('C3_315080500H_000224', '海角工作室', '整座工作室為藍白建築，工作室外被木香與大葉欖樹蔭所包圍。工作室的主人為黃天佑先生，其為漂流木木雕藝術家，來到這裡的訪客，可以學習木蜂蠟手工傢俱設 計製作、木工體驗及生態導覽，並喝上一杯主人親手泡制的咖啡。工作室另提供住宿，適合家庭式旅遊。', '花蓮縣974壽豐鄉鹽寮村橄樹腳133號', 974, '花蓮縣', '壽豐鄉', '886-9-20334586', '採預約制，如要參觀請前一天打電話預約；如想參與工藝體驗，請至少三天前打電話預約，工藝體驗四人可成行', '', '', '', '', '', '', '', '121.587000', '23.878000', 9, '', ''),
('C3_315080500H_000226', '味芳餅鋪', '味芳餅鋪是台東縣以甜點/手工餅為主的美食餐廳，老闆嚴國隆的兄弟均在國外做生意，為了陪伴父母以及傳承餅鋪生意，特別接手經營，旗魚餅、八嗡嗡有機餅、三仙餅都是曾經獲獎的產品。', '臺東縣961成功鎮中華路133號', 961, '臺東縣', '成功鎮', '886-8-9851838', '早上8:00~22:30隔週一休', '', '', '', '', '', '', '', '121.379900', '23.101270', 9, '', ''),
('C3_315080500H_000243', '055龍蝦海鮮餐廳', '位於台十一線13K處鄰近海濱處；餐廳堅持採用當地漁獲，餐廳規模可容納18～20桌，提供當地漁獲及龍蝦等新鮮海鮮料理及各類熱炒，是一間可眺望海景的龍蝦海鮮餐廳。', '花蓮縣974壽豐鄉鹽寮村132號', 974, '花蓮縣', '壽豐鄉', '886-3-8671055', '平日 11:00-14:30、16:00-19:30假日 11:00-15:00、16:00-19:30 每週三公休', '', '', '', '', '', '', '', '121.598270', '23.869830', 9, '', ''),
('C3_315080500H_000291', '海印民宿', '清晨，東岸早起的陽光鑽進被窩，陣陣的海浪聲讓我們捨不得再賴床，走出門外，迎面而來的是湛藍的太平洋。我們的海印，像兩顆白色的印章，枕在海岸山脈翠綠的臂彎裡。我們向大自然謙卑的學習，對週遭環境更加的友善，海邊的生活雖然簡單，但每天總能帶著滿滿的感動入眠。也許，有一天，在海印，在這東岸的一隅，您也能體會我們的感動。', '花蓮縣977豐濱鄉新社村新社212號', 977, '花蓮縣', '豐濱鄉', '886-9-26947800', '無', '', '', '', '', '', '', '', '121.532650', '23.639660', 9, '', ''),
('C3_315080500H_000299', '奇美部落廚房', '部落餐廳位於奇美文物館二樓，部落廚房位於部落餐廳旁一樓的傳統茅屋。在部落餐聽吹著徐徐涼風，享用好吃的奇美風味餐，堪稱一大享受。2008年奇美部落運用重點部落經費，以部落營造方式自力搭建完成。', '花蓮縣978瑞穗鄉奇美村五鄰2-10號', 978, '花蓮縣', '瑞穗鄉', '886-3-8991220', '奇美部落風味餐請於兩日前預約，因為我們還要去抓魚蝦、採野菜。', '', '', '', '', '', '', '', '121.457090', '23.510680', 9, '', ''),
('C3_315080500H_000300', '頭目海產店', '「頭目海產店」位於磯崎大鼻石山登山口的正前方，民國71年開業後直到今日，已傳承第三代。店內主要經營熱炒以及海鮮料理為主，主要魚貨、龍蝦等食材都是在地漁船出海捕獲。早期開業之初以「東海岸」為店名，服務客群也多為往來頻繁的卡車司機；後來因以「東海岸」為名的店家太多，加上老闆娘的阿公是歷經部落四任的頭目，於是更名為「頭目海產店」，客群也日漸以東海岸的觀光遊客為主。 頭目海產店的創始人-kiniw媽媽，除了餐廳部分以外，尚在餐廳旁販售自製的烤飛魚，產自磯崎在地海域的飛魚，經過清洗、泡鹽水的過程，再燒烤的時候，香氣四溢，吸引不少遊客聞香而來，生意好的時候，一天可以賣將近100條的飛魚呢!而kiniw媽媽的烤飛魚好手藝，更獲得原民會在民國100年的原住民產業博覽會中，榮獲「最佳人氣獎」的殊榮唷!店內的餐點除了熱炒以外，招牌和菜是七菜一湯的「山海風味餐」，由於經濟實惠，在地特色強烈，...', '花蓮縣977豐濱鄉磯崎村3鄰龜庵49號', 977, '花蓮縣', '豐濱鄉', '886-3-8711256', '10:00 - 14:00', '', '', '', '', '', '', '', '121.550420', '23.698320', 9, '', '大石鼻山停車場停車'),
('C3_315080500H_000329', '悟饕飯包', '位於豐濱鄉，地點位於台11線及台11甲線的交叉路口，位置明顯，提供過路觀光客熱騰騰便當及湯飲的服務站，也提供外送服務。', '花蓮縣977豐濱鄉豐濱村三民路22號', 977, '花蓮縣', '豐濱鄉', '886-3-8791174', '10:00 - 13:00、15:30 - 19:00，週日公休(暑假期間) 10:00 - 14:00、16:00 - 19:00，週日公休', '', '', '', '', '', '', '', '121.522480', '23.600390', 9, '', ''),
('C3_315080500H_000360', '好的，擺 手創藝術小店', '這幾年，台東都蘭一帶漸漸聚集了一群漂泊創作的藝文達人.... 「好的，擺」聚集了這些達人的創作品，以展、售的方式呈現在都蘭的一種生活狀態....', '臺東縣959東河鄉臺東都蘭村61-2號(糖廠內)', 959, '臺東縣', '東河鄉', '886-8-9531702', '10:00 - 18:00', '', '', '', '', '', '', '', '121.226340', '22.872170', 9, '', ''),
('C3_315080500H_000361', '大慶特產行', '大慶柴魚在成功鎮內，來到這玩必定要帶這的柴魚回去唷~有柴魚.香魚片.醉魚.旗魚鬆.魷魚鬆.魚骨頭.金針各項名產，歡迎電話訂購', '臺東縣961成功鎮中山路62號', 961, '臺東縣', '成功鎮', '886-8-9851133', '8:00-22:00', '', '', '', '', '', '', '', '121.379100', '23.102220', 9, '', ''),
('C3_315080500H_000362', '福和成糕餅店', '傳承三代的八十年糕餅店，秉持傳統、純手工、古早味Specialties passed down from three generations: Red Turtle Cake， Moon Cake， Mochi， Patty Cake， and Engagement Cakes， all with a sweetness that&#39;s never too rich.甘くて美味しい紅亀餅、月餅、お餅、引菓子など三代続く職人の味ー『福和成餅店』帶來福氣和成功的八十年糕餅店「福和成糕餅店」為台東成功鎮傳承三代的八十年糕餅店，堅持傳統手藝，保留食物最原始的美味，無添加任何防腐劑，做出最安心美味的產品；店內販售商品有封仔餅、花生糖、麻糬、紅龜粿、草仔粿、訂婚喜餅、粽子....等等季節應景食品；其中又以東部特色的綠豆椪古早味月餅「封仔餅」最受歡迎，自製獨特餡料和濃郁酥香餅皮...', '臺東縣961成功鎮中華路191號', 961, '臺東縣', '成功鎮', '886-8-9851432', '7:00 - 12:00 、 15:00 - 17:00', '', '', '', '', '', '', '', '121.381810', '23.101750', 9, '', ''),
('C3_315080500H_000364', '修圓玉石坊', '玉石專賣，修圓禪學與你分享。', '臺東縣959東河鄉東河村南東河195號', 959, '臺東縣', '東河鄉', '886-8-9896621', '09:00~21:00', '', '', '', '', '', '', '', '121.302730', '22.970000', 9, '', ''),
('C3_315080500H_000367', '都蘭山寶石工作室', '都蘭山寶石 DIY (鴻達)，位於台東縣東河鄉都蘭村，專門從事玉石加工，各國天然玉石批發、零售；更配合自助、團體旅遊，結合附近民宿，推出寶石 DIY 配套行程，教你認識寶石，請專家帶你到海邊檢寶石， DIY 自己親自研磨寶石。也可以安排參觀附近私房景點。', '臺東縣959東河鄉都蘭村44鄰458-2號', 959, '臺東縣', '東河鄉', '886-8-9531565', '09:00~17:00', '', '', '', '', '', '', '', '121.229270', '22.875830', 9, '', ''),
('C3_315080500H_000368', '棉麻屋', '屋主龍惠媚曾當選97年原住民駐村藝術家，近年來多次在國內外參展，深獲好評，最近將工作室設在故鄉隆昌村，將進一步造福部落， 創造更多的就業機會.棉麻材質為手工染織的自然纖維，以天然的果食為素材，種子為配件，取之於大地，珍惜自然資源.既環保又有美感，完美的手工織法，造型獨特，歡迎來住宿的朋友可順道造訪參觀選購', '臺東縣959東河鄉隆昌村162號', 959, '臺東縣', '東河鄉', '886-9-30989858', '10:00~18:00，週一店休', '', '', '', '', '', '', '', '121.268340', '22.939850', 9, '', ''),
('C3_315080500H_000371', '米苔目嫂海產店', '民國56年間，米苔目嫂一家來到成功鎮，一開始是賣冰維生，後來碰上一個客家的老婆婆跟他們說，賣冰只有夏天這個時機有賺頭，其他時節還得傷腦筋，於是便傳授製作米苔目的方法，讓米苔目嫂的傳奇就此展開。劉媽媽後來用攤車沿街叫賣，除了米苔目，還搭配一些小菜，在那個一碗米苔目賣一塊錢的年代，據說最高紀錄的營業額一天可以賣到一千多塊，以現在來講一天一千塊的工資已經算很好了，何況是那個錢很大的時候。當然，米苔目好吃自不在話下，除此之外，另外一個原因就是當時的成功鎮只有一家飯館和另外一家麵攤，大家的選擇也不多，漸漸的米苔目嫂的生意就這麼做了起來。', '臺東縣961成功鎮中山東路85號', 961, '臺東縣', '成功鎮', '886-8-9852635', '11:00~14:30；17:00~19:30每周二公休', '', '', '', '', '', '', '', '121.380520', '23.100580', 9, '', ''),
('C3_315080500H_000373', '月光小棧', '來到這裡建議你到旁邊的咖啡屋走走，一進門的吧台可是在地的人用漂流木創作的喔！再進去設有手創藝品區，這裡的作品全以&quot;手創&quot;呈現， 而不是大量工廠生產！因此鼓動本地的創作者，就算沒有購買，能幫忙宣傳或給予意見， 也是很大的鼓勵， 如果能以行動支持(當然是買下去啦！)，那當然是給創作者更大的支持和鼓勵囉！！', '臺東縣959東河鄉都蘭村46鄰20號', 959, '臺東縣', '東河鄉', '886-8-9530012', '11:00~17:00，週二、週三店休', '', '', '', '', '', '', '', '121.213810', '22.888720', 9, '', ''),
('C3_315080500H_000374', '金樽咖啡', '金樽咖啡位於金樽遊憩區，區內設有停車場及賞景步道，咖啡廳位於臨海制高點上，視野遼闊，點杯咖啡坐下來吹吹海風聽著浪濤拍岸，讓人感到無限舒暢。', '臺東縣959東河鄉七里橋11號', 959, '臺東縣', '東河鄉', '886-8-9541421', '09:30-18:00，週三店休', '', '', '', '', '', '', '', '121.275930', '22.936000', 9, '', ''),
('C3_315080500H_000375', '秋霞麵店', '秋霞麵店在都蘭糖廠斜對面巷子，開車約3-5分鐘就可以看得到，中午的生意不錯，不少在地人都會到這裡吃碗麵，乾淨整齊的環境也讓人喜歡 。', '臺東縣959東河鄉都蘭村38-5號', 959, '臺東縣', '東河鄉', '886-8-9531006', '11:00-14:00，週一、週五店休', '', '', '', '', '', '', '', '121.229260', '22.875900', 9, '', ''),
('C3_315080500H_000377', '富岡漁港量販活海產餐廳', '除了店裡面擺滿桌子，外面也都擺上了！櫥窗裏都是新鮮的海產，聽從地主的建議點菜準沒錯！', '臺東縣950臺東市富岡里富岡街270號1樓', 950, '臺東縣', '臺東市', '886-8-9280283', '09:00 - 21:00', '', '', '', '', '', '', '', '121.190970', '22.791950', 9, '', ''),
('C3_315080500H_000378', '港口海產店', '在30多年前，老闆娘為了生計及服務漁民便推著小餐車到富岡漁港賣著擔仔麵，70年代，觀光事業的發展及漁港的擴建，將原本擔子面的生意擴大經營改為海產店，為了能夠服務更多的觀光客及當地的饕客，於是開始了海鮮生意', '臺東縣950臺東市富岡里富岡街267號', 950, '臺東縣', '臺東市', '886-8-9281607', '11:00~14:00、16:00~19:30', '', '', '', '', '', '', '', '121.191450', '22.791930', 9, '', ''),
('C3_315080500H_000379', '特選海產店', '有許多的山菜是祖先所遺留下來的，在本店一一地提供給饕客們享用，有些菜餚在處理上是亟需講究，而有些菜餚是有季節性，所以饕客們想要享用，可預先來電諮詢或訂購，才不會失望而返。', '臺東縣950臺東市富岡街富岡路37號', 950, '臺東縣', '臺東市', '886-8-9281447', '08:30~14:00、16:30~20:00', '', '', '', '', '', '', '', '121.186400', '22.792770', 9, '', ''),
('C3_315080500H_000381', '宏津海鮮餐廳', '「宏津海鮮餐廳」的老闆兼主廚是一位擁有40多年廚藝經驗的師傅，年輕時全省跑透透的他曾在不少知名飯店服務，料理幾乎是他生命的一大部分，談起各種食材的變化他都能駕輕就熟。在因緣際會下來到了成功鎮的他，因為喜歡這裡的環境從此定居下來，而對於成功的旗魚料理，老闆也有一番研究。', '臺東縣961成功鎮民生路123號', 961, '臺東縣', '成功鎮', '886-8-9850539', '11:00-21:00', '', '', '', '', '', '', '', '121.380180', '23.102300', 9, '', ''),
('C3_315080500H_000570', '成功鎮農會農特產品展售中心', '本中心位於台11省道107.5公里處，北臨石雨傘風景區南靠三仙台風景區，營業內容全省農漁會和台東及在地農特商品，原住民手工藝品，另有本會農特產品三仙台白蝦及咖啡、台11旗魚鬆和牛肉乾，並設有花海區&#39;花田餐廳及咖啡工坊，可供遊覽車及遊客休憩旅遊咨詢.是您旅遊東海岸最佳休憩處，成功鎮農會歡迎您的來到來，我們將竭誠為您服務。', '臺東縣961成功鎮忠孝里美山路139-2號(忠孝國小前)', 961, '臺東縣', '成功鎮', '886-8-9871848', '08:00-17:00', '', '', '', '', '', '', '', '121.399970', '23.155730', 9, '', ''),
('C3_315080500H_000573', '台東地區農會元氣伴手館', '元氣伴手館位於台東市區，館內空間寬敞明亮，嚴選大台東地區知名伴手禮，埤南米、洛神系列、釋迦餅、飛魚餅、剥皮辣椒、杭菊、金針、酒類等，更匯集全台灣各地名特產，不定時推出最新產品及多項優惠商品，讓您到台東旅遊可以一次購足，打包台東特色及美好回憶!另埤南采風網http://www.puyyuma.org.tw/，也提供您遠距購物服務，讓您回到家也能輕鬆享受台東的優質農特產品!', '臺東縣950臺東市中正路356號', 950, '臺東縣', '臺東市', '886-8-9322906', '08:00-22:00', '', '', '', '', '', '', '', '121.151390', '22.757370', 9, '', ''),
('C3_315080500H_000574', '台東2626農村市集', '為了推廣台東農村及社區部落的優質產品，在鹿野鄉永安社區的號召下，由台東一群熱愛家鄉文化的民眾共同參與，決定於武陵綠色隧道，成立「台東2626市集」，不管是要來擺攤位，還是要到這裡找好康逛大街，都可以滿足大家的需求。台東是台灣最後一塊淨土，這裡有最純淨的環境，以及最善良的百姓，在這塊土地生產出最好最優質的農特產品，工藝達人們也生產出多樣有巧思的手工藝品，不過往往有好的產品卻常找不到消費者，同樣的，許多消費者找不到品質佳價格適宜的產品。有鑑於此，永安社區特地號召台東社區及民眾一起來推動「台東2626市集」，活動時間為每個月第2個星期6，下午2點至6點，於武陵綠色隧道，舉辦這個常態性的農村假日市集。台東2626市集，定位是一個農村假日市集，主題是多元開放式的，包括有機、環保、創意、在地生產、手作等，甚至會加入更多的休閒體驗活動及導覽解說。目前已有將近七十個攤位報名參加，來自池...', '臺東縣955鹿野鄉永安村永安路75號', 955, '臺東縣', '鹿野鄉', '886-9-19611644', '週六 14:00~18:00', '', '', '', '', '', '', '', '121.139240', '22.930410', 9, '', ''),
('C3_315080500H_000575', 'lala Ban新社香蕉絲工坊', '賦予傳承香蕉絲工藝文化的使命，取自原生材料的香蕉絲纖維，表達了噶瑪蘭族的精神與文化價值，尋求開創噶瑪蘭族文化產業的契機。1968年花東海岸公路(台11線)開工後，部落快速被捲入「現代化」，隨著各式物資的傳入，香蕉絲技藝步入式微。所幸地，在孕育噶瑪蘭族復明與文化復振運動的同時，老一被族人重新使起傳統地機織布的技法。從香蕉的種植、砍伐、刮絲、晾曬、分線、捻線、繞線、整經到編織等工序中，累積了深厚的技藝文化。', '花蓮縣977豐濱鄉新社村2鄰42號', 977, '花蓮縣', '豐濱鄉', '886-3-8711361', '08:00~17:00，預約專線:03-8711361', '', '', '', '', '', '', '', '121.538600', '23.655190', 9, '', ''),
('C3_315080500H_000576', '花蓮富里鄉農會', '富里羅山米廠展售中心，位於羅山有機村鄰近，於台九線旁310.5 k交通便利，停車寬廣、農產品眾多，服務態度親切。本農會以銷售富麗米為主、農特產品與名產類項目齊全，每年富里鄉的六十石山金針山花海一年遊客超過20萬人參訪，以及年末休耕期間，利用田間種植油菜花，並結合創意主題，營造大地花海，本會並提供接駁車服務，讓遊客能深入農村。展售中心服務功能齊全，且全面無休，不論吃、喝、玩、樂、休息一應俱全、另有提供製米的農村體驗，是過路客及團体遊覽車、自小客、鐵馬族、休憩的好地方。', '花蓮縣983富里鄉羅山村9鄰東湖6號', 983, '花蓮縣', '富里鄉', '886-3-8821705', '08:00~17:30花季(八、九月)延長營業時間至18:30', '', '', '', '', '', '', '', '121.247760', '23.175230', 9, '', ''),
('C3_315080500H_000577', '阿迪克漂流木藝術創作工作室', '生長於花蓮豐濱鄉磯崎村，阿美部落的阿迪克，不是原住民，卻有著原住民的靈魂，以台11線沿岸的漂流木為素材，擅長將即時閃現的豐沛靈感，為大自然的恩賜賦予新生命。「崇尚自然」與「實用」是阿迪克的創作理念，作品皆與生活息息相關，從日常生活用品，如:燈具(檯燈.吊燈.立燈)、餐具(湯匙.筷子.刀.叉.盤.杯墊)、廚房用具(鍋鏟.飯匙.桿麵棍.鍋墊)、時鐘&hellip;等，至居家空間整體設計，如:招牌、信箱、書架、展示架、桌椅、涼亭&hellip;等，處處皆展現他風味獨到的藝術手法，創意加上巧手，不起眼的漂流木，搖身變為精緻且獨特的手創藝術品！漂流木檯燈更是阿迪克重要的創作，從不刻意切割木頭，保留漂流木原來的形狀，為兼具實用性與自然性，在經過多次嘗試下，找出電線藏於木頭裡的方法，並選用可調整光度的調光器，能依照需求調整光線強弱，作為閱讀燈或是小夜燈，再搭配絲瓜、藤球、貝殼..等天...', '花蓮縣973吉安鄉海岸路360號', 973, '花蓮縣', '吉安鄉', '886-9-37079115', '09:00-21:00', '', '', '', '', '', '', '', '121.590940', '23.951760', 9, '', ''),
('C3_315080500H_000578', '東里碾米工廠－御皇米', '得天獨厚覓淨土˙百年稻香繫真情 東里碾米工廠位於花蓮縣富里鄉，花東縱谷的中心點。18世紀末的台灣花蓮，東里碾米廠第四代傳人宋鴻琳的曾祖父，獨具慧眼的來到花東縱谷秀姑巒溪源頭落腳，並引溪水動力椿米、打米，一畝畝的墾荒地為良田。用心關懷每一寸土地，細心照顧每一株稻苗，代代傳承曾祖父的理念，於是我們延續出了跨越一世紀的故事。東里碾米工廠成立於清光緒26年(西元1899年)，至今已是傳承五代，經營有109年的悠久歷史，從日據時代和二次大戰，未有電力時的水車動力樁米，至今佔地四千餘坪的輾製廠房和兩千餘坪的恆溫倉儲腹地；足足走了超越了一整個世紀之久，堪稱台灣最老的碾米工廠，長期受到在台日籍政要人士的喜愛，尤其是日本明治天皇更是讚不絕口。東里碾米工廠的前身是「大莊水車」的上水車、下水車兩家碾米工廠，經歷了百年傳承，在歷經四代的經營下早以不只是一個單純的米廠，更成為花蓮、富里鄉東里地區...', '花蓮縣983富里鄉東里村大莊路1號', 983, '花蓮縣', '富里鄉', '886-3-8861171', '08:00 - 17:15', '', '', '', '', '', '', '', '121.303710', '23.267860', 9, '', ''),
('C3_315080500H_000579', '農郁米舖', '&ldquo;農郁&rdquo;是指鄉下農人們濃郁的人情味，用濃濃的珍惜情感呵護、栽種，希望每一次收成的好米，能讓每一位享用到的饕客都能有滿滿的幸福與健康，所以取名做&ldquo;農郁&rdquo;。這裡以米為主，以其他農產為輔，從一開始只有販賣家用米，一直到應饕客要求開展的手工禮品系列，無外乎就是希望滿足每一位來到米舖的饕客們的願望，冀望所有人都健康幸福也希望大家都能看到、聽見這些農產背後的故事。為傳統的農業穿上不一樣的新衣，讓所有的包裝有他的價值，讓不起眼的後山農業可以以不同的姿態，展現它豐富的多彩的創意得有價值的另一面，是我們一直的宗旨。創作可以再利用的&ldquo;布包裝&rdquo;，賦予米不一樣的精品價值，從打樣板、挑選布料、設計一直到成品，全部都是MIMI和米媽親力親為完成的。店內大部分布提都是使用日本棉麻布為素材，設計感以及顏色印染都比較優秀、花樣多元、...', '花蓮縣970花蓮市和平路592號', 970, '花蓮縣', '花蓮市', '886-3-8310499', '9:00 - 12:00 、 13:30 - 18:00，假日店休', '', '', '', '', '', '', '', '121.599250', '23.979480', 9, '', ''),
('C3_315080500H_000580', '大哥的故事', '大哥ㄉ故事創立於2002年，以趣味，搞笑及可愛ㄉ大哥造型結合而的紀念商品專賣店。距加油站約500公尺，遠處就可以看到以大哥為主題ㄉ招牌，店內擺設以監獄為主題，兩間牢房供遊客免費拍照留念，所有商品都以綠島風光與大哥做發想，特別具有紀念價值。首創為綠島的天氣幹好熱，掀起T恤文字風潮！如果您要來綠島度假，別忘了來探監！如果您來探過監了，大哥依然在綠島等您再次探訪！', '臺東縣951綠島鄉南寮村漁港路27-1號', 951, '臺東縣', '綠島鄉', '886-8-9671086', '無', '', '', '', '', '', '', '', '121.473610', '22.662390', 9, '', ''),
('C3_315080500H_000581', '花蓮觀光糖廠', '光復鄉，一個位於花東縱谷的鄉間小城，剛插秧的水田倒映出翠綠的山色天光，火車穿過寧靜的田野將隆隆聲傳得好遠......。光復糖廠高大的煙囪是這裡的地標，舊舊的製糖工廠刻劃著歲月的痕跡，停閉的工廠內塵埃在透進的光束中飛舞，伴著幾隻鴿子空盪盪的呼嚕聲；早期的日式木構造建築部分整修後成為特色的旅館提供住宿，安靜的時光佐著清新的空氣，曾經，在開工時期，這裡是人聲鼎沸的不夜城，空氣中總是飄著甜甜的糖香。糖廠裡另一項人氣指標就屬那香甜的冰淇淋，採用馬鍚山純淨無汙染的天然山泉水，以及花東縱谷在地生產的新鮮乳品等上等食材製成，好吃的程度從每天排隊購買的人潮就可以看得出來，有朝一日經過光復，一定不能錯過。', '花蓮縣976光復鄉大進村糖廠街19號', 976, '花蓮縣', '光復鄉', '886-3-8704125#200', '08:00-20:00', '', '', '', '', '', '', '', '121.420940', '23.655770', 9, '', ''),
('C3_315080500H_000582', '曲禾酒莊', '曲禾酒莊位於台東縣卑南鄉初鹿休閒農業區，為台東縣第一座合法農村酒莊，因地勢偏高，空氣清晰、氣候怡人，酒莊內種植梅樹、桂花、枇杷和釋迦等，曾榮獲2009年十大神農獎傑出農民的酒莊主人廖坤郎以自產的梅子、桂花、枇杷、肉桂等釀造成五種口感各異的酒，並加工生產梅醋、脆梅、軟梅和米酒頭等產品，其中最特殊的是肉桂香腸及枇杷花茶包，都是在層層把關中精心嚴選 再嚴製成完美的風味。來到酒莊，可以一睹釀酒過程，或採梅子、摘琵琶，體驗DIY釀酒，而酒莊旁邊有櫸木林生態區，可以到林區內走走，吸收森林芬多精，依時令可以賞蝶、看螢火蟲，和觀賞獨角仙生態，也能騎著鐵馬悠遊其間。目前酒莊另外還提供露營服務，歡迎各位與親朋好友在忙碌之餘，一起結伴來曲禾酒莊享受愜意的山野風情。因為一般申請酒莊大約半年時間就可以走完所有行政流程，不過曲禾酒莊卻花了4年，由於是台東縣政府第一次接到酒莊申請案件不敢冒然開放，其...', '臺東縣954卑南鄉明峰村鹿鳴橋2鄰29-3號', 954, '臺東縣', '卑南鄉', '886-8-9570561', '08:30~16:30', '', '', '', '', '', '', '', '121.108020', '22.867870', 9, '', ''),
('C3_315080500H_000583', '花蓮好事集', '花蓮好事集成立於2010年12月4日，一群熱愛土地的農夫們成立了屬於花蓮在地的小農市集。英文agriculture乃土地文化的意思，花蓮好事集定位為友善耕作農夫市集，農耕方式強調對環境與土壤友善的生態文化。好事集將遵循參與式保障體系(Participatory Guarantee System，簡稱PGS)之驗證精神，不使用農藥與化肥，以誠信為本的政策，在互相信任的基礎上，建立透明和公開性，減少層次剝削和行政干預。同時，好事集為一學習型市集，透過參與式保障體系(PGS)，以共同願景、參與、透明度、信譽、學習過程、分享，彼此學習成長。並透過各項田間參訪、體驗等活動，拉近人與土地的距離。花蓮好事集除了提供環境友善的產品，也期望成為產地體驗與農場生態旅遊的平台，讓消費者透過體驗，建立生產知識與飲食文化，促進鄉村產業轉型；並且加入更多元綠色消費的活動，深植花蓮永續環境的概念。除...', '花蓮縣970花蓮市福建街460號', 970, '花蓮縣', '花蓮市', '886-9-37079115', '每周六09:00~12:30', '', '', '', '', '', '', '', '121.610120', '23.972340', 9, '', ''),
('C3_315080500H_000584', '雲山水villa home', '雲山水佔地24公頃，園區內有廣大棕梠科植物及落羽松，季節性開花植物與大片的翠綠草皮，人工湖泊，湖光倒影，宛如歐洲鄉間的湖濱森林。雲山水Villa home在建築外觀上以南歐白牆紅瓦拱窗外觀為參考依據，在室內空間部分，營造南歐鄉村田園風格，採用自然的原木材質，大地的自然色調，樸質的歐洲進口陶磚，花草圖騰的織品窗飾，老舊又有手感的家具燈飾，是空間中的主角，將這些巧妙的搭配組合以營造出南歐鄉村的田園風格幸福度假屋。', '花蓮縣974壽豐鄉豐坪路二段二巷201弄18號', 974, '花蓮縣', '壽豐鄉', '886-3-8656060', '08:00 - 22:00', '', '', '', '', '', '', '', '121.513870', '23.826970', 9, '', ''),
('C3_315080500H_000585', '莫內花園咖啡農莊', '莫內花園〈安提哥〉咖啡農莊主要是經營廣受讚譽的布蘭赫亞咖啡，以及散發人文氣息的民宿。直接由瓜地馬拉火山山脈的Balanjoyo咖啡莊園，進口優質極品咖啡生豆，再由園丁老張每天鮮量烘焙。因此，整座園區都呈現瓜地馬拉那種融合浪漫拉丁及神秘馬雅的風格。中美洲熱帶雨林的造景，點綴以許多來自馬雅遺址的文物飾品，民宿客房連家具也都是馬雅工人手工製作的，床罩則是馬雅人最知名的織錦。這種浪漫拉丁與神秘馬雅的風格，只有以印象派的手法才能貼切淋漓表現。園丁老張和他學建築的兒子合力規劃設計，就是以印象派的表現手法，呈現自然樸拙，卻散發朦朧幽雅的美感。當然，園區裡也設置了一座蓮池，使人聯想到印象派宗師莫內的名畫蓮池，因此，取名為莫內花園。', '花蓮縣974壽豐鄉林園路3-2號', 974, '花蓮縣', '壽豐鄉', '886-3-8642243', '24小時營業', '', '', '', '', '', '', '', '121.505130', '23.922550', 9, '', ''),
('C3_315080500H_000586', '林田山社區咖啡館', '位於中山堂下方的咖啡館，由原本的老檜木房舍改建而來，據說當時是高級幹部才可以居住的地方唷！雖然內裝相當的新穎，不過依然有著不少日式房舍的簡約風格。遊客要進入即要配合日式房舍的規矩，脫鞋子在木板地上行走，小小的茶几式桌子搭配昏黃的燈光、以原木改建的溫馨感，與角落古早家具相互輝映。而望出窗 外盡入眼簾的就是那些日式建築構成的社區，雖然不若京都那般俯拾皆是的舊房舍，但林田山確實保持了在台灣島上日本社群所留下的完整畫面，讓人似乎回到那電 影畫面中的一幕。若能享用些簡單的餐點配上咖啡度過一個午後，也不失為到花蓮旅遊悠閒腳步停駐的一棧。', '花蓮縣975鳳林鎮森榮里99巷46號', 975, '花蓮縣', '鳳林鎮', '886-3-8751688', '09:00 - 17:00', '', '', '', '', '', '', '', '121.397140', '23.717030', 9, '', ''),
('C3_315080500H_000587', '山中傳奇', '「山中傳奇」得天獨厚的地理位置，體驗山色海景環繞四周的景觀，天然芳多精環繞身體每個細胞，享受天然又健康的的森林浴，完全的放鬆自我，讓自已悠遊於天地之間，從一早迎接晨曦的到來，暖暖的陽光照耀每片大地，享受一份不同往日悠閒的早餐，細細品嚐美食與森林般的清新之旅吧！入夜後，俯瞰整片景色，星光與夜燈相互呼應著，如夢似幻般的銀河互相輝映，當沉溺在其中時甚至會分不清自已是否置身人間…', '花蓮縣970花蓮市石璧街26號', 970, '花蓮縣', '花蓮市', '886-3-8581188', '15:30 - 23:30', '', '', '', '', '', '', '', '121.558670', '24.001630', 9, '', ''),
('C3_315080500H_000588', '立德布洛灣山月村', '立德布洛灣山月村座落於太魯閣布洛灣的上台地，四面環山、立霧溪旁切而過，景致渾然天成。山月村擁有2.5公頃的土地，僅有19棟，共37間的小木屋，整體規劃類似村莊聚落，除了有濃厚原住民原始風格外，還帶有一點傳統唐式的懷舊味道。房間內的原住民織布吊燈及布畫都是純手工製作。最特別的是客廳移到室外，白天可在客廳乘涼欣賞周遭美景，晚上可與家人談天觀星。房間型態有景觀山月套房、景觀部落套房及景觀頭目套房三種。景觀山月套房計28間，約8坪，內部規劃為兩張單人床(可住1~4人，超過2人以上須另加舖墊)；景觀部落套房計8間，約12坪，每人一張單人床，適合五到八人住宿，適合家族及學生團體；景觀頭目套房僅1間，約25坪，內分為主臥室及起居室兩間，2套衛浴設備，主臥室為一張雙人床，起居室可另加舖墊提供親友入住。', '花蓮縣972秀林鄉富世村231-1號', 972, '花蓮縣', '秀林鄉', '886-3-8610111', '24小時', '', '', '', '', '', '', '', '121.572120', '24.171060', 9, '', ''),
('C3_315080500H_000589', '如豐琢玉工坊', '如豐琢玉工坊是一家傳統的台灣玉石飾品加工廠，在豐田聚落裡從事玉石加工已快40年頭了，早期多是以外銷為主，近幾年來，因大陸市場崛起，本想打算關門大吉，只是內心深處戀戀著台灣玉，捨不得放棄。還好準備歇業的同時，看到了經濟部一鄉一特色輔導專案的資訊，透過財團法人石材暨資源產業研究發展中心團隊頂力相助，重新起步，重新出發，轉型做DIY玉石體驗工廠，期望把台灣玉石飾品加工的在地產業，能永續傳承。第二代已回鄉傳承在地產業，各位貴賓到花蓮旅遊時，別忘了到&rdquo;如豐琢玉工坊&rdquo;來，親自體驗磨玉的樂趣，磨一塊自已打造的玉飾品，帶回家做永久的紀念及收藏，玉可以保平安哦！＂非常歡迎各位鄉親的蒞臨&mdash;DIY＂。', '花蓮縣974壽豐鄉豐山村忠孝街91號', 974, '花蓮縣', '壽豐鄉', '886-3-8652323', '09:00 - 17:00', '', '', '', '', '', '', '', '121.495100', '23.852390', 9, '', ''),
('C3_315080500H_000590', '太魯閣晶英酒店', '以傲人之姿，座落於花蓮太魯閣國家公園境內山岳型的國際五星級渡假酒店，乃為晶華國際酒店集團旗下之飯店。本飯店座落於中部東西橫貫公路之天祥地區，伴立霧溪貫穿其間，秀麗山色即是天然的藝術品，室內採現代中國風的設計風格，以山、水、石、木等元素作為妝點空間的點綴品，與大地共生共存共榮，營造低調、沉穩、高品味的渡假休閒氛圍；全館擁有160間客房，規劃行館樓層與一般休閒樓層，旅人可依需求選擇合適下榻之客房；提供中西料理服務，廚藝團隊大量運用花蓮食材，滿足您崇尚在地、美味與視覺等多重饗宴。任何季節來訪，太魯閣皆有不同的景色，靜歇於飯店的舒適空間裡，面峽谷、望山嵐、聆聽溪水協奏曲，讓能量在千年峽谷裡轉化昇華，恣意享受來自於太魯閣迴響的清新與暖意', '花蓮縣972秀林鄉天祥路18號', 972, '花蓮縣', '秀林鄉', '886-3-8691155', '24小時', '', '', '', '', '', '', '', '121.494210', '24.182130', 9, '', ''),
('C3_315080500H_000591', '花蓮美侖飯店', '1993年，美侖飯店伴隨著花蓮的心跳誕生。身為花蓮第一家國際型渡假飯店，美侖始終與花蓮一起呼吸律動，坐落於湛藍海洋與蓊鬱群山環抱中的美侖，是通往花蓮山水美景的鎖鑰，無論是景致明媚的太魯閣、風景宜人的七星潭、或是集合美食與絕佳伴手禮的花蓮市區&hellip; 美侖，絕對是您花蓮之旅的最佳起點～ 不只如此，無限寬廣的藍與綠垂手可得，更讓美侖不但是一都市的度假休閒飯店，更成為花蓮最跳躍鮮明的印記！不但是地理上的美景中心，更是花蓮歷史的、人文的、感官的中心。以自然美景為本，用在地文化提味，美侖灌溉了身心的感官需求。現在，就開始享受美侖，開始你在花蓮心的起點。', '花蓮縣970花蓮市林園1-1號', 970, '花蓮縣', '花蓮市', '886-3-8222111', '24小時營業', '', '', '', '', '', '', '', '121.627890', '24.000100', 9, '', ''),
('C3_315080500H_000594', '知本泓泉溫泉渡假村', '泓泉溫泉渡假村的溫泉，是來自1460公尺深的地底，在泉質上，自然要比附近挖鑿數百公尺深的溫泉，來得更加純淨優越。為維持溫泉井管的通暢，泓泉溫泉渡假村在每個星期裡，都會利用清早或午後，便將這座位於露天溫泉浴池畔的地熱井打開，讓溫泉與蒸氣直衝雲霄，在陽光照射下，蒸騰的白色水霧，隨著和風吹拂，明顯出現絢麗的七色彩虹，「虹泉」之名不脛而走，乃成為知本溫泉區特有的景觀。由於「知本一號地熱井」的位置，就在泓泉溫泉渡假村的「彩虹廳」內，如此純正優質的溫泉水，不論您是在客房內泡湯，或是在露天溫泉SPA浴池、室內溫泉按摩池，都可以享受到真正的「知本第一泉」。', '臺東縣954卑南鄉溫泉村龍泉路139巷1號', 954, '臺東縣', '卑南鄉', '886-8-9510150', '24小時', '', '', '', '', '', '', '', '120.999230', '22.690560', 9, '', ''),
('C3_315080500H_000595', '松園別館', '松園別館約興建於二次世界大戰末期，日治晚期民國32年間，原為日本海軍兵事部辦公室，日本戰敗撤退後，民國36年改由陸軍總部管理、陳誠青年軍駐守；後又成為「美軍顧問團軍官休閒度假中心」；66年改由國有財產局所有，67年交由行政院退輔會管理。由於松園地理位置優越，可遠眺太平洋與海岸山脈，園內又有百年老松成群，在民國85 年退輔會計畫以「旅館建地」出售，遭地方人士與藝文團體激烈反對，在各界努力爭取保留之下，終於在89 年7 月13 日由花蓮縣政府編定為「歷史風貌專區」。民國90年獲文建會閒置空間再利用試辦點補助後，最後於91年9月23日公告登錄為歷史建築，並在隔年11月14日宣佈正式對外開放。而後松園由花蓮縣政府委託民間經營，於民國92年至94年委託花東文教基金會，95年至今委託祥瀧股份有限公司經營管理，經營內容包括:園區建築及環境之維護、安全管理、清潔及松樹週遭生態保護等，定...', '花蓮縣970花蓮市松園街65號', 970, '花蓮縣', '花蓮市', '886-3-8356510', '09:00 - 18:00', '', '', '', '', '', '', '', '121.606390', '23.972220', 9, '', ''),
('C3_315080500H_000597', '花蓮翰品酒店', 'Chateau是法文代表貴族所居住的城堡，de chine則意指中國，別出心裁的logo設計，則是源於歐洲貴族的盾牌(coat of arms)。中古時代的歐洲，有很多有地位的家族或人，設計自己的盾牌勳章，用以表示家族歷史或權力。而翰品的盾牌Logo特別融合了龍紋和天圓地方等中國元素來傳達中西合併的美。以精品設計旅館概念打造各式風格的酒店，讓您在商務及渡假的旅程中，都能享受到極致細膩的服務。目前全台灣共有三家翰品酒店，分別為代表歐式風格的翰品酒店新莊、綠能樂活的翰品酒店花蓮及品味時尚的翰品酒店高雄。歐式風格　品味獨具　翰品酒店新莊以Art Nouveau為設計主軸，採優雅時尚的法式風格，融合低調奢華的東方美學，展現出充滿活力的傳統之美。戶外歐風廣場為概念，四周牆面包覆著歐式藝術銅雕，飯店內了融合浪漫歐洲街頭的氛圍。走進大廳，仿佛進入歐洲小鎮溫暖靜謐而舒適的環境，讓旅人在...', '花蓮縣970花蓮市永興路2號', 970, '花蓮縣', '花蓮市', '886-3-8235388', '24小時營業', '', '', '', '', '', '', '', '121.626470', '23.991820', 9, '', ''),
('C3_315080500H_000599', '牛山‧呼庭休閒區', '關於呼庭‧About Huting牛山，也就是牧牛耕種之地，我們阿美族稱為呼庭 (Huting)。曾幾何時，成群結伴的牛便少了，靠牛來耕種的畫面也不再了，現在依稀可見的是野放山上優遊自在玩耍的牛群 ，於是呼庭在我們阿美族人的心裡有了另一個意義:一個自由自在、無憂無慮的所在地，也就是我們經營這塊原地所懷抱的想法。而此刻當你來到這塊土地，同樣被這樣的山這樣的海這樣的自然美景所感動時，跨越種族與時空，你已經成了這塊土地的守護神，但願這塊土地，能讓你感受到心靈的寧靜與空曠，成為你生命中的淨土。隨著季節流轉，牛山給你不一樣的美，三月荊桐和春天一起醒來，為大地點綴鮮紅﹔四月百合，在山坡望海，清香綻放﹔梅子綠了，火金姑來探路了，幽綠在向晚的山裡，點亮一片光。五月走，一起去看飛魚﹔七月來，舞一場生命的豐年祭....，接下來呢，秋涼冬送風，牛山不冬眠，只給你一季舒眠，一覺醒來，陽光在海角...', '花蓮縣974壽豐鄉水璉村牛山39-5號', 974, '花蓮縣', '壽豐鄉', '886-3-8601400', '10:00～18:00', '', '', '', '', '', '', '', '121.560830', '23.767300', 9, '', '園區停車(收費)'),
('C3_315080500H_000600', '近月旭海民宿', '近月旭海曾是電視偶像劇「東方茱麗葉」借景拍攝異國風情的場景。因為這兒離海只約200公尺，砂灘邊，綠地上的這一棟墨色斜屋頂，搭配著典雅日本外壁磚的歐式小洋房，露台上設有白色咖啡座，屋外芳草如茵，蕉風椰影，花團錦簇，在在散發出一股慵懶自在的悠閒氣息，令人宛如置身於南歐愛琴海畔或是印度洋中馬爾地夫般的浪漫。「近月旭海」位於台灣東海岸國家風景區內，傍山(海岸山脈東側)向海(太平洋左岸)佔地約3000坪的庭園建築，圈圍出一個自成一格，景觀天成的海濱樂園。在此無論是要聽濤、逐浪、觀星、賞月，或看旭日東升，雲彩幻化，舟楫來往，鷗鷺翔飛，或是品嚐別有風味的咖啡與餐點，或僅在此看海發呆，都會讓人俗慮頓消，感覺幸福無比。這樣幸福的園地，隨時等待有緣人來共享喔！', '花蓮縣974壽豐鄉鹽寮村鹽寮5鄰152號', 974, '花蓮縣', '壽豐鄉', '886-3-8671158', '24小時營業', '', '', '', '', '', '', '', '121.598100', '23.870320', 9, '', ''),
('C3_315080500H_000601', '遠雄悅來大飯店', '該飯店係由世界級頂尖設計的WAT&G公司，以浪漫的維多利亞式的華麗與豐富融合太平洋海岸的寧靜，迎賓大廳設計挑高28米、規劃有391間。所有客房均採四季恆溫空調，客房樓層設有新穎的連通門可合併空間使用，讓旅客依個人喜好而選擇，縱情享受維多利亞時期當代的繁華與感官，所有客房皆有觀景陽台、讓渡假的悠閒心情更臻完美。該飯店周遭圍繞著林蔭步道與觀景平台串接環繞，不僅連結所有戶外景點與室內設施，詩人花園的雕像造景，綠廊的盎然綠意，火舞廣場的黃昏市集及戶外現場表演，各有巧思、各具特色。東向可一覽無遺湛藍的太平洋、北邊為繁華的花蓮市區及花蓮港、西邊是碧綠的太魯閣山脈縱谷，南邊可見秀麗東部海岸線及海洋主題樂園。位在嶺頂(標高220公尺)高處，有391間客房，以維多利亞宮殿為主題意象的五星級渡假旅館，擁有獨特絕佳的視野景觀。旅客容納:1000人公共設施:國際會議廳(600人)、皮爾沙KTV...', '花蓮縣974壽豐鄉鹽寮村山嶺18號', 974, '花蓮縣', '壽豐鄉', '886-3-8123999', '無', '', '', '', '', '', '', '', '121.596650', '23.908190', 9, '', ''),
('C3_315080500H_000602', '檜木居', '「檜木居」全區以檜木為主題訴求，建築體包含了五種檜木，搭配日本進口綠建材，優雅的和風造景庭園，營造最細膩的休憩空間。房內檜木床頭板散發芬多精的香氣及能量，並特別提供檜木精油洗髮精及沐浴乳讓您沉浸在檜木香裡。專屬SPA芳療室(需預約)除了按摩，更搭配了檜木遠紅外線烤箱/蒸氣室，讓您疲憊的身軀得到完全的放鬆。寬敞明亮的紅酒區，供應國外酒廠精釀葡萄酒，讓您與三五好友在晚餐之餘小酌談天。早餐的檜木咖啡更帶給您一天滿滿活力。使您在享受度假時身心靈也都充滿能量。', '花蓮縣973吉安鄉慈惠一街58號', 973, '花蓮縣', '吉安鄉', '886-3-8546535', '07:00-22:00', '', '', '', '', '', '', '', '121.584980', '23.976210', 9, '', ''),
('C3_315080500H_000603', '遠雄海洋公園', '花蓮，人稱台灣的後山，卻是一塊擁有著世界級景觀的瑰麗珍寶。為了讓世界上更多人能享受這片好山好水，遠雄企業團秉持著經營公共事業般的嚴謹服務精神，以海洋為中心主題，集休閒、娛樂、養生、教育、環保、文化等知性與感性特質於一體，打造頂級國際休閒勝地水準的花蓮遠雄海洋公園與遠雄悅來飯店。期望以花蓮為起點，串聯起遠雄企業團橫跨北中南的環島遊憩鏈，提升台灣整體休閒品質，帶動現代人保育養生的新里程，並為台灣這座婆娑之洋中的美麗之島，留下一個呼吸的出口，一個生生不息、永續演進的最佳註腳。藍天、白雲、森林、海洋，是您一踏入花蓮遠雄海洋公園暨遠雄悅來大飯店即可感受到的自然風光。陽光中的遠紅外線可以促進體內有害物質的代謝；背倚海岸山脈，豐沛森林資源充滿著植物特有的芬多精；浩瀚的太平洋一覽無際，含大量負離子的海風輕輕吹拂，舒緩了都市生活的壓力與忙碌。徜徉在花蓮遠雄海洋公園暨遠雄悅來大飯店，與大自...', '花蓮縣974壽豐鄉鹽寮村福德189號', 974, '花蓮縣', '壽豐鄉', '886-3-8123199', '09:30 - 18:00暑假營業時間:週一至週五09:30~18:00　　　　　　　週六至週日09:00~18:00', '', '', '', '', '', '', '', '121.603500', '23.901230', 9, '', ''),
('C3_315080500H_000604', '境外漂流民宿', '你已經有遠走境外　與放逐漂流的準備了嗎我們以為　必須要放下熟悉的生活模式與態度　才能感受到遠走境外的喜悅我們認為　必須要鼓起勇氣的做出選擇　才能有放逐漂流的自由', '花蓮縣974壽豐鄉鹽寮村福德65號', 974, '花蓮縣', '壽豐鄉', '886-9-28671403', '24小時營業', '', '', '', '', '', '', '', '121.600850', '23.883560', 9, '', ''),
('C3_315080500H_000605', '山嶺好望角民宿', '歡迎來到花蓮好望角 海景、夜景、山景、港景、霧景、船景、海灣、日出頂級會館。全世界最漂亮、治安最好、最便宜的頂級總統套房就在這裡。喜歡的話請幫我們按個讚！', '花蓮縣974壽豐鄉鹽寮村山嶺9-6號', 974, '花蓮縣', '壽豐鄉', '886-9-32581151', '24小時營業', '', '', '', '', '', '', '', '121.595600', '23.904720', 9, '', ''),
('C3_315080500H_000606', '日暉國際度假村', '夢想的入口 Villa‧溫泉‧冷泉‧稻香‧森林浴日暉國際渡假村-台東池上，座落於花東縱谷中心，北與花蓮縣富里鄉相鄰，南接關山，幽靜山景中，得天獨厚的地理位置能同攬花東勝景、每間客房皆提供碳酸氫鹽氯化物泉，開幕三年即成為台灣東部新興熱門的渡假飯店。HOTEL區共119間客房，皆備有景觀露台，輕推開窗便能飽覽花東湛藍天空、綠意滿溢的山嵐景致。總統VILLA別墅區共有102間Villa，為東南亞地區最大Villa群，每棟別墅Villa均擁有景觀庭園、私人游泳池、發呆亭；舒適明亮的客廳及獨立臥室，享受不被打擾的私密空間。入住日暉國際渡假村，彷彿到達一個美麗夢境。※榮獲交通部觀光局花東縱谷風景區管理處「2009縱谷優良飯店首選」是花東縱谷地區獲得票選第一名的飯店業者。※2010年首創台灣「Super Car Tour超跑假期」，建立縱谷新文化、超跑在日暉；連續三年舉辦，將台東觀光...', '臺東縣958池上鄉新興村新興107號', 958, '臺東縣', '池上鄉', '886-8-9861111', '24小時營業', '', '', '', '', '', '', '', '121.198730', '23.105490', 9, '', ''),
('C3_315080500H_000608', '讚炭工坊', '位在花蓮縱谷的鳳林小鎮，山腳下有個歡樂窩，他是劉校長用生命向歲月兌換的第三個春天，在這個新天地中，劉家七口以夢想為藍圖，齊心合力，營造出一片充滿愛與希望的健康事業【讚炭工房】一個發人深省，讓人讚嘆的生命傳奇。故事的主角是劉校長，他的第一個春天，是服務長達四十五年的教育事業，絳帳和風，桃李無數！第二個春天，在他退休之後奉獻給家鄉的老人會，客屬會與槌球會等十年之久深耕鄉土，發揚客家文化！如今，人生進入下半場，劉校長認為應該回歸簡單生活，並與家人兒孫共享天倫的時候了，然而，前年竟發現罹患初期腸癌，經緊急開治療，目前已無大礙。但是他的子女們卻醒悟了，決定往後要共同營造一個能使爸媽安享晚年，並讓家人情感相繫的健康美樂地。於是，劉校長的五名子女及女婿、媳婦們，在徵得爸媽同意後，便在山下農地闢建農舍與工房，這一舉動，不但開啟了健康團聚的田園生活，更成為讚炭工房的發展契機。第一個竹炭窯...', '花蓮縣975鳳林鎮正義路15號', 975, '花蓮縣', '鳳林鎮', '886-3-8763488', '09:00- 17:00', '', '', '', '', '', '', '', '121.446480', '23.759070', 9, '', ''),
('C3_315080500H_000609', '福樟紅檜木屋', '山‧海與藍天東海岸的蔚藍海洋離我們好近、好近...，用視覺、味覺...還有心來體驗東太平洋-福樟之美，分享福樟每一個角落的每一刻感動；給自己充裕的時間探索，這才是旅遊的真諦。晨‧日光沿著蔚藍的海岸線道路，溫暖的光線指引著遊客來到福樟紅檜木屋渡假村光線的投射下，棕櫚樹、木屋、木製桌椅，十足的南洋味吸引著過往的旅人，福樟的晨，打開窗便可看見日出，這對都會人來說，是一種奢求，但也是一種誘因。', '臺東縣961成功鎮信義里豐田路27-5號', 961, '臺東縣', '成功鎮', '886-8-9841639', '無', '', '', '', '', '', '', '', '121.343680', '23.050430', 9, '', ''),
('C3_315080500H_000610', '知本老爺大酒店', '知本老爺大酒店位於台東縣卑南鄉溫泉村的知本風景區內，除了擁有溫泉外，尚有峽谷、瀑布、森林遊樂區，是一個多元化的風景區，客房共計183間，均有大型景觀窗面向綠意盎然的山林景觀，飯店內亦有多項的休閒設施、室內外各型的溫泉浴場、專業的SPA服務、精心料理的餐飲服務及完善的會議設備，能使前來休閒渡假的客人真正體驗知本老爺貼心真誠、以客為尊的優質享受。本酒店各式客房共計183間，均有大型景觀窗面向綠意盎然的山林景觀，飯店內亦有多項休閒設施、是內外各型的溫泉浴場、專業的spa服務、精心料理的餐飲服務以及完備的會議設備，能使前來度假的各位客人真正的體驗知本老爺貼心真誠、以客為尊的優質感受。', '臺東縣954卑南鄉溫泉村龍泉路113巷23號', 954, '臺東縣', '卑南鄉', '886-8-9510666', '24小時營業', '', '', '', '', '', '', '', '121.006970', '22.691760', 9, '', ''),
('C3_315080500H_000611', '東河戀咖啡小館', '東河戀咖啡小館座落在東海岸最美的海灣:都蘭灣的中心，杉原護漁區上方，擁有寬闊的觀海平台，日出月昇的絕佳觀賞處，濃厚香醇的咖啡，純手工烘焙製作的糕點麵包，主廚精心烹調的歐式料理，我們用心的經營這個地方，希望來到東海岸的遊客可以在此盡情欣賞美麗的海景，吹著舒適的海風，聽著浪濤聲，品嚐我們準備的餐點，就算在此靜靜的待個一下午都很值得的！', '臺東縣954卑南鄉富山村杉原30號', 954, '臺東縣', '卑南鄉', '886-8-9281276', '24小時營業', '', '', '', '', '', '', '', '121.186910', '22.837660', 9, '', ''),
('C3_315080500H_000612', '東河部落屋', '「東河部落屋」位處台東縣東河鄉台11線花東海岸公路132K處，北往南左轉、南往北右轉，循小徑進入約500公尺就可以抵達。傳承先人智慧及後代工法搭建了傳統的茅草部落屋，遠眺東河新橋的鮮紅壯麗及海岸山脈的清新秀麗。東河部落屋現址是由瑪路阿瀧文化發展協會所經營管理，這裡除了做為遊客體驗阿美族傳統建築與文化外，還經營住宿、原住民風味餐、竹筏遊馬武窟溪等，是一處讓遊客可以接觸大自然，又能瞭解阿美族文化的休閒聖地。', '臺東縣959東河鄉東河村12鄰南東河326號', 959, '臺東縣', '東河鄉', '886-8-9896183', '全數採取預約制，請至少三天前訊息傳送預約，經雙方合意回覆確認後登錄完成預約', '', '', '', '', '', '', '', '121.302720', '22.969890', 9, '', ''),
('C3_315080500H_000614', '天龍溫泉飯店', '天龍溫泉飯店位處於一個既神祕又特別的、經常雲霧奔騰、暖泉氤氳的山谷中，也就是位在花東縱谷的裙襬。無論你從花蓮或台東行走美麗的花東縱谷公路(台9線)在池上或關山轉彎進入台20線(南橫東段)，很快的你就會沉浸於新武路峽的大景並陶醉其中。開車只需約40分鐘車程，你就會看到一座彩虹般的吊橋橫跨霧鹿峽谷，矗立在吊橋畔的建築物就是全台灣唯一的峽谷溫泉飯店，靜靜座落山嵐邊，沒有吵雜的人聲，唯有潺潺溪流聲和青綠山景，充滿悠靜清新的氣息。峽谷岩壁獨特湧出型溫泉及飯店淡雅簡潔的原住民風格空間，讓您在自然的環境中，輕鬆卸下疲勞的身心。飯店旁有一條建於日治時期的天龍古道，沿途有豐富的生態資源與古道的幽情，還有每天最受住客歡迎的晨曦霧鹿峽谷導覽解說，帶您去看難得一見的霧鹿V字峽谷、千年溫泉鐘乳石、溫泉噴泉及溫泉山壁。欣賞峽谷的美，泡著與大自然結合的優質露天溫泉，彷彿人間仙境&hellip;', '臺東縣957海端鄉霧鹿村1之1號', 957, '臺東縣', '海端鄉', '886-8-9935075', '24小時營業', '', '', '', '', '', '', '', '121.041900', '23.173110', 9, '', ''),
('C3_315080500H_000616', '鹿鳴溫泉酒店', '鹿鳴溫泉酒店 七星映照之吉地鹿鳴溫泉酒店位處都蘭山麓的縱谷平原上，東方依著海岸山脈，西臨中央山脈高台龍田，為一開闊性的河階台地，四周群山環繞，山色隨著四時而變化，白雲蒼狗，氣象萬千，坐臥於鹿鳴溫泉酒店的視野所及盡是青翠與湛藍。鹿鳴溫泉酒店建築設計概念來自「與自然共存」強調順應自然、簡潔、樸實，外觀幾乎融入背景環境中。酒店圓弧形外觀以兩條平行弧線為主，於建築物核心向外延伸與旁邊的鹿野溪相呼應，形成匯聚東西兩面山系地氣，上承北斗星垂拱映照之「七星映照吉地」！都蘭山乃台灣唯一蘊藏藍寶石的聖山，藍寶石為玉之上者，玉有涵養水氣之功，因而都蘭山頭常見雲霧裊繞，即使大晴天亦然，靈氣薰人；更有甚者，藍寶內蘊於山中，千萬年來吸收宇宙能量精華，散發出有益於人體的磁場特強，鹿鳴溫泉酒店得天獨厚，盡得宇宙能量磁場聚合之利。鹿鳴溫泉源自中央山脈鹿野溪紅葉谷，與紅葉溫泉同源，通稱「鹿鳴紅葉溫泉」，...', '臺東縣955鹿野鄉中華路一段200號', 955, '臺東縣', '鹿野鄉', '886-8-9550888', '24小時營業', '', '', '', '', '', '', '', '121.101330', '22.897940', 9, '', ''),
('C3_315080500H_000617', '吉拉卡樣有機共同農場', '「吉拉卡樣」的美麗在於沒有汙染的自然，「吉拉卡樣」的自然在於有機耕作的堅持。這裡的美麗，夏夜沁涼的風中伴隨閃閃的螢火蟲最自然。這裡的自然，四季生生不息有機蔬菜最豐富。吉拉卡樣是花蓮193縣道54公里處的美麗瑰寶，這裡因為部落長老的有機堅持，多年來保持著天然美麗與自然樸實。這裡有種豐富的有機蔬菜，小黃瓜、四季豆、玉米、青椒…等，近年來更致力於有機栽種「花蓮１號黃豆」，以有機天然的方式種植最健康天然的農產。為歡迎更多人來享受部落的美麗，成立部落餐廳提供部落的有機風味餐、有機水餃、開闢露營區…等，夏天來到這裡，記得要去探訪滿天星光的螢火蟲，還有沁涼的小溪等著和你一起清涼。來吧！享受一下「吉拉卡樣」的天然美景、品味一下「吉拉卡樣」的有機健康！歡迎來「吉拉卡樣」熱情一下。', '花蓮縣975鳳林鎮山興里山興路38-6號', 975, '花蓮縣', '鳳林鎮', '886-8-9741300', '08:00 - 17:00', '', '', '', '', '', '', '', '121.495160', '23.713260', 9, '', ''),
('C3_315080500H_000618', '兆豐農場', '遊客中心農特產區主要販售當地各式名產供遊客購選，並有原住民風味館及紀念品館。農特產品:原住民小米酒、山葡萄露、米麻糬、牛軋糖、牛奶糖、沙其瑪等。紀念品:花東精緻玉石商品販售、兆豐休閒服、兆豐休閒帽、駝鳥蛋、乳牛造型杯、兆豐紀念郵票、兆豐鑰匙圈等。線上農產品展示:鮮奶冰淇淋、鮮奶酪線上紀念品展示:乳牛牛仔帽、乳牛牛仔帽、乳牛造型帽、兆豐休閒帽、兆豐休閒服、駝鳥蛋、駝鳥蛋、駝鳥蛋、造型相框、造型相框、造型相框、乳牛造型杯、恐龍氣球、乳牛氣球、造型存錢筒、乳牛存錢筒冰品區:遊客中心冰品區可提供遊客品嚐香濃咖啡、鮮奶酪、鮮奶冰淇淋，是您享受下午茶休憩及觀賞歐式花園景觀的最佳去處。另有販售各式冷熱飲、各式口味鬆餅、烤吐司及花茶供遊客多樣選擇。', '花蓮縣975鳳林鎮永福街20號', 975, '花蓮縣', '鳳林鎮', '886-3-8772666', '08:00 - 17:00', '', '', '', '', '', '', '', '121.456400', '23.803720', 9, '', ''),
('C3_315080500H_000619', '知本國家森林遊樂區', '知本森林遊樂區地處台東縣卑南鄉溫泉村，知本溪流經森林遊樂區南側。區內山巒疊翠、林木蓊鬱。其中以鳥類資源最為豐富，是台東市郊賞鳥的最佳據點。植物則以原生之觀賞花木為主，且有大片純林，是遊樂區內主要森林景觀。另外園內設有好漢坡、野餐桌椅、森林浴步道、榕蔭步道、水流腳底按摩步道等，供遊客到此放鬆身心，享受大自然的樂趣。好漢坡因坡度陡峻而得名，長度雖僅320公尺，但落差卻有150公尺，共計792階，為考驗耐力與衝勁之最佳場所。上到坡頂沿榕蔭步道健行，到處可見千根榕，甚為壯觀，是本區一大特色。此外，園區內種植東部特有原生種蝴蝶蘭，希望遊客漫步園中步道享受森林浴外，也可欣賞優雅的蝴蝶蘭花。＊遊客服務中心為提供完整的資源解說服務，特於區內建設遊客服務中心，其內部設施包括森林生態教育展覽室、森林走廊、多媒體放映室、多媒體自動查詢機等解說設施。＊定向運動場域本園區設有定向運動場地，目前世...', '臺東縣954卑南鄉溫泉村龍泉路290號', 954, '臺東縣', '卑南鄉', '886-8-9345493', '7:00~17:00(7月、8月延長開放時間至18:00)', '', '', '', '', '', '', '', '120.992100', '22.686960', 9, '', ''),
('C3_315080500H_000621', '立川漁場', '頂著兩個太陽，兩個月亮，坐落於花東縱谷的立川漁場，擁有絕佳的養殖環境、源源不絕的天然湧泉，堅忍不拔、順勢而為的漁人精神，讓我們知道感恩，得人如魚。ISO22000的認證品牌，嚴格檢驗為消費者把關，成為品質保證，也通過CAS標章及GAP優良水產養殖戶；讓每一顆黃金蜆都有身份證，更是花蓮縣優良的無毒養殖戶，任何產品，我們都達到新鮮、無毒、健康的立川標準摸蜆仔兼洗褲，體驗當年的童趣生活，是一種經驗的傳承，一種文化；蜆殼DIY，從動手作的樂趣開始與美學碰撞，接觸生命，從漁鄉美饌到五餅二魚，精緻的立川招牌料理，「黃金蜆、貴妃魚、臺灣鯛」，立川三寶，精采上桌。期待用愛與您分享每一份收穫的恩典！', '花蓮縣974壽豐鄉共和村魚池45號', 974, '花蓮縣', '壽豐鄉', '886-3-8651333', '08:00 - 20:00', '', '', '', '', '', '', '', '121.522180', '23.864770', 9, '', ''),
('C3_315080500H_000622', '初鹿牧場', '初鹿牧場位於明峰村，位處高度約200至390公尺的台地上，因坡度不大，排水良好，且氣候溫和雨量豐沛，終年可供應牧草，是經營牧場的絕佳環境。園區寬乳牛是主要的經營目標，也是初鹿牧場特色之一。乳牛原產於加拿大，且以高品質的苜蓿草餵養，因此能生產出香濃可口 ，品質絕佳、味道獨特香濃的鮮乳，巧克力調味乳和煉乳也別具風味。來到初鹿牧場一定要嚐嚐用初鹿鮮奶製成的冰淇淋和冰棒，香醇可口的風味令人難以忘懷。除此之外，牧場設有露天茶座、露營烤肉區和森林浴步道區 ，並規劃有乾草製造區、青割牧草區和放牧飼養區，提供遊客體驗牧場風情。初鹿牧場視野遼闊，觸目可及的青青牧草，置身其間全身舒暢，終年遊客不斷，是相當受到歡迎的旅遊景點。為因應旅遊觀光人潮，初鹿牧場附近的飯店、旅館、民宿，都別具特色積極建設美麗的景觀，吸引更多的外來遊客，體會令人難忘的初鹿風情。', '臺東縣954卑南鄉明峰村牧場1號', 954, '臺東縣', '卑南鄉', '886-8-9571002', '08:00~17:00', '', '', '', '', '', '', '', '121.108300', '22.866000', 9, '', ''),
('C3_315080500H_000623', '東河休閒農場', '民國79年政府創導休閒農業，經農委會同意東河成立休閒農業區，並由農委會委請嘉義農專負責規劃。農場首先就舊有七、八莊舍及分場部員工宿舍整建為民宿，闢建樟樹林步道，民國80年開始營運。起初來訪遊客與本場員工共用餐廳，體驗榮民生活，該七、八莊舍至今仍在東河遊憩區內，欲體驗榮民早期生活者不妨來此參觀。至86年觀光局納入輔導補助，整建餐廳、渡假小屋(現為別墅區)等設施，農場設備漸臻完善。於87年改善通訊設備、路燈照明設施，延建樟樹林步道(即現之生態步道)，新建涼亭、花架等休閒設施，使遊憩區設施完備。民國89年拆除共育室改裝之和室新建桃莊，90年整建獨棟之桃莊庭院提供遊客更好的住宿設備，並整建兒童遊戲區供兒童遊玩。至今，東河休閒農場仍與周圍榮民眷屬維持良好關係，時而可見附近榮民至本場販賣現採水果，鮮嫩多汁，吮指回味。東河休閒農場本身則配合政府政策，致力於農業轉型無毒有機農業，配合觀...', '臺東縣959東河鄉北源村67號', 959, '臺東縣', '東河鄉', '886-8-9891193', '08:00 - 21:00', '', '', '', '', '', '', '', '121.300360', '23.074740', 9, '', ''),
('C3_315080500H_000624', '(青田)銀川永續農場', '銀川永續農場於1996年成立，以『銀川』品牌行銷，為全台灣最大有機水稻種植團隊。法人公司為青田農產有限公司，致力於有機農產品於國際市場銷售與國內銷售通路的經營。農場與公司皆通過慈心有機農業發展基金會驗證，擁有有機生產與有機加工、分裝驗證。本農場備有展示場，供遊客休憩、咖啡點心，另預約有體驗活動課程與農家餐可享用。以下為本公司有機驗證資料與獲獎記錄。●有機驗證資歷: 台灣慈心有機農業發展基金會有機農產品及農產品加工驗證 台灣屏東科技大學有機農產品產銷履歷驗證 美國USDA有機認證 通過ISO22000及HACCP雙驗證●獲獎記錄 總經理獲1998中華永續協會傑出事業成就獎 總經理獲1999全國十大傑出青年農民 總經理獲2009年全國十大神農 青田農產有限公司2011年獲農委會第二屆菁創獎 總經理獲2011年度獲選農委會產銷履歷達人 銀川永續農場2012...', '花蓮縣983富里鄉富里村公埔路二段75號', 983, '花蓮縣', '富里鄉', '886-3-8831232', '08:00~12:0013:30~17:30', '', '', '', '', '', '', '', '121.244600', '23.178500', 9, '', ''),
('C3_315080500H_000625', '瑞穗牧場', '瑞穗牧場瑞穗牧場秉持著一份對這片土地與故鄉的熱愛，民國89年轉型觀光休閒牧場，期盼讓更多國人享用到超優質的乳製品，秉持著當日現擠鮮奶呈獻給遊客一份誠摯樸實的熱情，精心研發的「鮮乳酪」更是瑞穗牧場用心製作的產品之一，最新鮮的鮮奶全程低溫製作，精心挑選110公克的容器讓您的味蕾最高點劃下完美的驚嘆！說到乳製產品值得一提的是鮮奶饅頭，傳統的饅頭在瑞穗牧場的用心研發下使用西點工法揉製而成，口感綿密濃濃乳香，是您值得一試的好滋味。為了提升花東伴手禮種類選擇，瑞穗牧場耗費一年的研發，於民國93年推出「瑞穗牧場乳牛軋」系列產品，甫推出即獲得遊客肯定並在2005年獲得「第一屆國家設計金獎」、「台灣包裝之星」、「世界包裝之星」等榮耀。瑞穗牧場一路走來只有「執著」兩字，堅持打造一塊淨土;堅持呈現最樸實無華的熱情; 如同瑞穗牧場的英譯名稱RARE SEED(珍貴的種子)。也歡迎國人路過花東縱...', '花蓮縣978瑞穗鄉舞鶴村157號', 978, '花蓮縣', '瑞穗鄉', '886-3-8876611', '平日:09:00-17:00，每周三公休周休假日:08:00-18:00連續假日:06:00-18:00', '', '', '', '', '', '', '', '121.345270', '23.479960', 9, '', ''),
('C3_315080500H_000626', '原生應用植物園', '台東位於台灣東南隅，東臨太平洋暖流帶，雨量充沛且分佈平均，土質、水質、氣溫非常適合各類生物之生存，早期是日據時代即為日本人藥草重鎮，諸此在在證明台東是台灣的藥草王國，亦是藥草的故鄉，有著無窮無盡的寶藏。台東原生應用植物園就是在如此優渥的「天然條件」及迎合「時代趨勢」孕育而生的，是一座將農業結合了健康、休閒、生技等概念的活體展示館，更是全國第一家致力將藥草應用在生活產品上的健康產業，希望能藉著園區提升台東的農業，並將發揚台東原生藥草造福人群。園區內目前利用有機方式栽種了大約兩百多種的原生藥草植物，藉由專業的解說，希望能傳達給遊客原生藥草植物與人類密不可分的關係。座落於花東縱谷南端初鹿牧場旁的園區，除了擁有得天獨厚的國際級山海景緻，更兼具『健康、休閒、知性、生技』等功能，讓您徜徉在五星級飯店式的建築硬體中，欣賞國際級的原野風光，還有專業的導覽解說人員帶領著您一起探索兩百多種...', '臺東縣954卑南鄉明峰村試驗場8號', 954, '臺東縣', '卑南鄉', '886-8-00385858#11.12', '08:30 - 20:00', '', '', '', '', '', '', '', '121.104220', '22.853350', 9, '', ''),
('C3_315080500H_000627', '原野牧場', '原野牧場 - 牧羊人的故事當春風吹起，和絢的海風，喚來了滿山遍野的百合與牽牛花，牧羊人總是迎著和風，趕著一群羊覓草 &hellip;&hellip;當夏風吹起，熾熱的海風沸騰著，卻也勾引著人群投向他的懷抱。牧羊人總是揮著汗，趕著一群羊覓草 &hellip;&hellip;當秋風吹起，迎風鳥逆著蕭瑟的海風飛翔，七星海泛起一片漣漪，牧羊人總是踩著枯葉，趕著一群羊覓食 &hellip;&hellip;當北風吹起，澎湃的海潮激起濃濃的海霧刺骨的冬季裡，牧羊人依然頂著東風，趕著一群羊覓食 &hellip;&hellip;一天牧羊人在七星海畔築夢，與老原住民和藝術家陳立，用漂流木花了三百多天完成了充滿羊奶咖啡香的歡歡仙美樓，在濃濃的心、濃濃的情、濃濃的奶香中，原野牧場已經與您共度十年的美好時光，始終山與海的對話沒改變，羊奶咖啡的濃郁口感沒改變，七星海依然湛藍，牧場主人的情依然純真。', '花蓮縣971新城鄉七星街9號', 971, '花蓮縣', '新城鄉', '886-3-8226778', '09:30 - 19:00', '', '', '', '', '', '', '', '121.633460', '24.024310', 9, '', ''),
('C3_315080500H_000628', '馬太鞍文史工作室', '馬太鞍是一個阿美族(邦查)部落的稱呼，位在花蓮縣光復鄉馬錫山山腳下，早期是一群阿美族人生活在馬太鞍溪與烏卡蓋溪(現今大興溪)形成的一塊沖積扇地形，後來因鹽水港製糖廠(現今花蓮糖廠)的設立，在原來靠北的土地上廣植蔗糖原料－甘蔗，而使得當地的阿美族人不得不往南遷移。', '花蓮縣976光復鄉大全鄉大全街42巷15號', 976, '花蓮縣', '光復鄉', '886-3-8700015', '09:00-18:00', '', '', '', '', '', '', '', '121.412850', '23.658300', 9, '', ''),
('C3_315080500H_000629', '那魯灣手藝工坊', '我們是來自花蓮，數多年來從事文化創作工作，目的是推廣與強調文化的重要性，在當代設計中，文化符號以成為社會關注的重要焦點，也因為文化是我們出生所賦予的背景，裏頭包含許多以前的老一輩留給我們的教導，在文化風俗方面有很重要的狩獵技術、成衣製作、歌舞競技，以及原住民與主靈的重要慶典，這些都是我們必須保留的，如果沒有文化，台灣文化符碼就很難找到一個定位，所以在文化市場上，除了產品優勢，我們更期望讓文創設計與工藝品在現代生活上能夠與文化產生互動連結，讓更多人能夠認識屬於台灣的地方文化。我們全心投入文化事業多年，主要推廣阿美族文化與古拉斯先生的漂流木創作燈飾，結合在地元素與材料，展現出截然不同的創作面貌給予各位視覺上的享受。我相信，當台灣所有設計師與文創者一同持續努力匯聚起來，有朝一日，無論在世界上任何地方提到台灣，人們腦海中都能夠顯現出台灣美學的印象，讓台灣設計文化影響台灣生活美學...', '花蓮縣973吉安鄉仁里村仁里5街3號1樓', 973, '花蓮縣', '吉安鄉', '886-3-8541411', '09:00-18:00', '', '', '', '', '', '', '', '121.592590', '23.969080', 9, '', ''),
('C3_315080500H_000631', '吉籟獵人學校', '吉籟，源自阿美族母語CIDAL，不僅為太陽之意，在母系社會的阿美族裡，更象徵著母親。因此，吉籟獵人學校不單擁有滿滿的熱情和活力，更期許自己能如母親守護孩子一般，守護這塊土地、同時照護部落裡的人們。原住民的傳統文化隨著現代化逐漸被忽略忽視，而產業結構的改變，讓部落族人原本的生活方式受到嚴重衝擊，迫使年輕人不得不離鄉出外工作，也讓固有的傳統文化難以保存。有感於此，我們成立了吉籁獵人學校，本著傳承、延續和守護的初衷，積極發揚原住民的傳統文化，並將其中內含的寶貴知識、技能、生活智慧以及與大自然和平相處、共存共榮的價值觀傳遞給下一代，更期盼藉由傳統與休閒旅遊的結合，能把新的生機帶進部落，促進在地的就業機會外，也讓社會大眾能用不同的角度了解原住民文化、了解這塊土地。吉籟獵人的體驗活動囊括山與海的領域，山林體驗活動包括:生火、達路案製作、植物辨識、生態導覽、狩獵陷阱製作、組合弓製作、...', '花蓮縣974壽豐鄉水璉路181號', 974, '花蓮縣', '壽豐鄉', '886-9-86997778', '08:00 - 17:00', '', '', '', '', '', '', '', '121.561300', '23.783520', 9, '', ''),
('C3_315080500H_000632', '鳳林鎮文史工作協會-花手巾植物染工坊', '本協會成立於民國93年，成員多為退休校長、老師、公務人員及家庭主婦，以研究蒐集整理鳳林鎮文史文物資料及推展鄉土教育活動為宗旨。秉持對社會教育的專業知識潛心研究地方文化，並協助鳳林鎮地方文化館營運及文化推廣諮詢服務等，期使客家文化達到傳承、教育、保存功能。鳳林鎮花手巾植物染工坊簡介:啟用於98年1月22日，在鳳林鎮文史工作協會努力下，得到鳳林鎮公所支持，將昔日常有人聚賭的菸樓舊模型建築，變身為展售客家媽媽們植物染製品，同時藉由鳳林數位中心的功能，整合社區文化產業業者及公、私部門，推動產業結盟，並協助地方開發特色商品，結合地方文化館場(如校長夢工廠、客家文物館、菸樓文化聚落等)以電子化商務行銷技術，促進社區文化產業觀光特色，帶動鳳林商機。本協會還將上述特色，設計套裝旅遊行程，安排專業導覽解說員，您可以結識10人以上，實際參與植物染DIY體驗及遊賞鳳林人文采風。(所謂「植物染...', '花蓮縣975鳳林鎮中華路164號', 975, '花蓮縣', '鳳林鎮', '886-3-8760905', '08:00 - 17:00', '', '', '', '', '', '', '', '121.451490', '23.747500', 9, '', ''),
('C3_315080500H_000634', '瑞源花布燈籠生活文化館', '繁花朵朵處處開─來自後山的花布燈籠2002年的仲夏夜，在台東鹿野的瑞源社區裡，因為一盞老舊花布燈籠的身影，客家子弟從長輩的記憶中，重溫那段早已流逝的暖暖回憶。為了重新點燃客家溫情，在社區發展協會的努力下，終於找到讓幸福萌芽的希望基地，塵封長達18年之久的農民教室裡，一個代表客家希望之光的概念逐漸成型&hellip;花布燈籠工場選擇在台東後山建造全台獨一無二的花布燈籠博物館，手工打造的大花燈籠，一一懸掛在博物館內，由社區男丁負責手工磨製木作骨架，由社區媽媽親自剪裁縫紉布花外衣，於是一盞盞純手工打造的燈籠，以象徵吉祥富貴的傳統鄉村人情味，重新點亮客家文化傳承的新風采。歷經三年勞委會補助社區加多元就業我們花布燈籠初步成果:現已研發出各種形狀(有八角、橢圓、長圓、短圓)、大小尺寸、手提、吊掛、等不同的產品共數種(花色則是以客家花布為主)分到送至總統府、行政院、立法院、考試院、中...', '臺東縣955鹿野鄉瑞源村同興路156號', 955, '臺東縣', '鹿野鄉', '886-9-32664401', '週一~週五 08:00 - 17:00', '', '', '', '', '', '', '', '121.156600', '22.955530', 9, '', ''),
('C3_315080500H_000635', '瑪法琉工作室', '卡地布部落(台東知本)，是由Mafaliu (瑪法琉)、Pakaruku (巴卡露固) 、Ruvaniaw(路法尼奧)三個家族所組成的大型部落。  工作室的男女主人皆從事原住民傳統文化創作工作，都屬於瑪法琉家族的成員，於是取瑪法琉之名成為工作室名字，藉此希望能夠發揚過去家族的榮耀。', '臺東縣950臺東市豐田里博物館路1號', 950, '臺東縣', '臺東市', '886-9-32662030', '如想參觀請至國立台灣史前博物館紀念品店內服務專線:(089) 381166*701營業時間:09:00-17:00', '', '', '', '', '', '', '', '121.091360', '22.760690', 9, '', ''),
('C3_315080500H_000636', '一粒工作室', '小小月桃，肩負傳承使命ili一粒工作室 終於開始成立了自己的部落格，以往都是透過記者、文史學者、研究生的採訪、介紹，蓽路藍縷才得以讓獨特而稀有的月桃作品與世人見面。為了延續阿美族這項技藝絕活，我們將在此園地，詳盡地介紹 ili 是如何巧妙地將台東本地的植物中最具有韌性的月桃，如何地編織、開發、創作成各式各樣的作品，讓我們的生活用品回歸最真誠質樸的原始觸感。編織，是阿美族美麗的代名詞ili，是一個傳統的阿美族婦女，有一顆善良的心，總是熱心地去幫助別人；曾幾何時，對傳統編織產生一股莫名的興趣，無法抵擋。編織在阿美族傳統生活裡，是一個重要的文化象徵，它是阿美族美麗的代名詞，它代表著阿美族祖先傳承下來的智慧，傳承是一份沉重的工作，更是一份責任，是對祖先的承諾，也是對後代子孫的叮嚀，唯有不斷的傳承，才能實現優質的作品。月桃編織，織入重要文化內涵從傳統的織布到月桃編織，ili發現了...', '臺東縣950臺東市勝利街24巷27弄2號', 950, '臺東縣', '臺東市', '886-8-9343959', '無', '', '', '', '', '', '', '', '121.156550', '22.762080', 9, '', ''),
('C3_315080500H_000637', '凱應原住民工作坊', '凱應-阿美族-自青少年時期即從事服飾製作及改良，迄今已有30餘年，因對原住民族文化的熱誠及情有獨鍾，激勵了製作及創新原住民固有的傳統服飾，並於民國87年【凱應原住民工作坊】負責人～林金花老師成立了台東第一間的藝術創作工作室，以原住民傳統服裝為主要工作項目。隨著與族人們的生活跟著長輩學習傳統織布及刺繡，日積月累的成就一手好技藝。我們應用多樣且豐富色彩，變化出各種美麗的服飾，也受到族人及長輩們的肯定，也漸漸地開啟了【凱應】傳承文化之門。凱應原住民工作坊一針一線，繡的都是阿美族的生活和文化，原住民沒有文字，但針、線就是我的筆，我要把文化一一記錄在繡布及服飾上，代代相傳。【商品種類】原住民傳統服飾、刺繡服飾改良式服飾、頭飾、飾品等。', '臺東縣950臺東市更生路856號', 950, '臺東縣', '臺東市', '886-9-11190078', '09:00-17:00', '', '', '', '', '', '', '', '121.134880', '22.769620', 9, '', ''),
('C3_315080500H_000638', '鐵花村．慢市集', '台東是原住民音樂人才及創作能量最豐沛的地方，人們在鐵花村的演出中感受山海文化孕育出的澎湃歌聲與悠閒自在，這裡也是音樂傳承、培育與交流的舞台，可以聽到重量級歌手的演出、新生代的熱情以及更多的可能。從週三到週日每天都有現場的音樂演出可以欣賞；每逢週五到週日還有慢市集，集結廿多個手工文創及在地小農，週末在戶外的草地上則有輕鬆的草地音樂及不定期的手作教室。讓更多人可以藉此體會東部的慢活、自在與悠閒。除了音樂演出外，鐵花村還供應輕食飲料及下午茶，並網羅在地藝術家的原創產品以及值得你深入認識的台東好物，人們也可以在此買到鐵花村獨一無二的文創產品。即使沒有演出的鐵花村，以在地藝術創作結合大片綠草地，處處都是悠閒迷人的好風景。老樹、大片的綠色草地及開放自在的氛圍，每每吸引不少遊人駐足，藝術家安聖惠、阿水、見維巴里&hellip;等人以漂流木、金屬、石頭、竹子等自然質材的創作及畫作，更豐...', '臺東縣950臺東市新生路135巷26號', 950, '臺東縣', '臺東市', '886-8-9343393', '鐵花小舖 週三~週日 14:00-22:00假日慢市集 週五 18:00-22:00、週六日 16:00-22:00 表演時段  週三~週六 20:00-22:00、週日 19:30-21:00', '', '', '', '', '', '', '', '121.146200', '22.753370', 9, '', ''),
('C3_315080500H_000639', '高第國際藝術有限公司', '高第彩色玻璃為客戶提供精緻及特殊化的彩色琉璃建材，如屏風和門窗等:並積極拓展藝術玻璃在公共藝術及藝術品展覽上的發展。開發各式各樣的彩色琉璃禮品、機關團體獎座贈品及珠寶玻璃及飾品。我們引進全美最大彩色玻璃供應商及其教學系統，推廣彩色藝術玻璃，開設一系列玻璃教學，培養更多的玻璃設計製造人才及師資。2008年開始，本公司接連榮獲為花蓮縣立新城國中、花蓮縣立和平國小、審計部台南縣市審計室及財政部北區國稅局花蓮縣分局新建工程工共藝術之指定委託創作團隊，未來將積極結合其他材料進行整體設計，朝文創產業與公共藝術方向發展。', '花蓮縣973吉安鄉中央路一段651號', 973, '花蓮縣', '吉安鄉', '886-3-8528383', '09:00～17:00(週日休)', '', '', '', '', '', '', '', '121.564790', '23.961070', 9, '', ''),
('C3_315080500H_000640', '九鳥陶燒工作室', '位於台東初鹿新班鳩的九鳥陶燒工作室，取自地名裡的&rdquo;鳩&rdquo;字。工作室有各類型的燒陶工具，包括電窯、瓦斯窯及柴窯，不同的燒窯方式可以表現不同的創作特色，進行不同族群與不同藝術文化之間的重組，發展創作的可能性和獨特性，營造一個多元交流的藝術平台，激發源源不絕的創作動力，累積台灣本土文化藝術厚實內涵，提昇整體藝術水平是「九鳥陶燒」的陶藝精神。工作室的藝術家是排灣族的廖光亮老師，從事陶藝製作至今已有十八年的時間，這段期間一直專注在陶的各種專業知識與施作上，所有的創作主軸皆以｢陶｣為出發，希望能營造原住民藝術家自由創作與交流的空間，落實藝術文化在地實現。每天皆有開放民眾參觀、參與陶藝DIY活動，另有九鳥咖啡，配上藍天白雲與大自然的美景，是休假不錯的去處。', '臺東縣954卑南鄉初鹿村新班鳩42號', 954, '臺東縣', '卑南鄉', '886-8-9570550', '09:00-17:00', '', '', '', '', '', '', '', '121.067680', '22.847660', 9, '', ''),
('C3_315080500H_000643', '三立冰淇淋', '離開了奉獻30年的台糖光復糖廠冰店，潘媽媽(陳阿玉)在52歲開始自己創業，不同於以往的製作材料，潘媽媽用上了花蓮最引以為傲的牧場鮮乳為基底，賭上累積了大半輩子的製冰經驗來製作冰淇淋，因為覬覦著花蓮鳳林水源的純淨甘甜，三立冰品店就從鳳林火車站前的一個小角落出發，開始了一個不一樣的冰品生命。原本只是代工製作花蓮唯一的鮮乳冰淇淋，守著一方小小的店，直到女兒克紹萁裘擴大經營，潘媽媽也做得更起勁了，除了鮮奶口味的冰淇淋，陸續又研發了榴槤、南瓜、酪梨、蜂蜜&hellip;.等滿滿一櫃子各種鮮美好吃的新口味，更區隔了原本傳統單純的剉冰，把黑糖、芒果、鮮乳等各種鮮果元素放到剉冰裡，讓吃剉冰這種解渴的冰品變得更可口也更有趣。在花蓮鳳林開業20餘年來，潘媽媽希望讓花東地區在地的農產品用精緻冰品的方式讓所有人共享，除了堅持衛生第一，嚴選食材，三立冰品店也不斷嘗試創新口味，以滿足遊客挑剔的味蕾...', '花蓮縣975鳳林鎮中正路二段263號', 975, '花蓮縣', '鳳林鎮', '886-3-8762932', '10:00 - 21:00', '', '', '', '', '', '', '', '121.450480', '23.750690', 9, '', ''),
('C3_315080500H_000644', '廣盛堂', '廣盛堂羊羹是花蓮縣玉里鎮的特色伴手禮，羊羹聞名至今已有六十年之久，馳名全省、享譽東部。羊羹主要原料有:紅豆、砂糖、洋菜粉。最大特色在於素食、香甜Q滑、甜而不膩、風味佳；羊羹是屬鄉土名產，成為來東部旅遊的遊客需購買的伴手禮。廣盛堂羊羹從一九六七年以來，參加海內外許多食品大賞，並獲得無數的肯定。 1967年 榮獲日本優良食品獎 1986年 榮獲韓國食品展覽獎 民國74年 花蓮名產展示會『甲等獎』 民國77年 花蓮特產食品評鑑會優勝紀念『優等獎』 民國78年 年度花蓮縣農會特產品名產評鑑會『特優獎』 民國100年 榮獲名產烘焙衛生GHP評鑑優良', '花蓮縣981玉里鎮中山路二段82號', 981, '花蓮縣', '玉里鎮', '886-3-8882569', '08:00-22:00', '', '', '', '', '', '', '', '121.314970', '23.333680', 9, '', ''),
('C3_315080500H_000645', '紅瓦屋老地方文化美食餐廳', '位於花蓮縣光復鄉的「紅瓦屋老地方文化美食餐廳」，是馬太鞍地區的第一間餐廳，營業已逾27個年頭，是一家兼具藝術氣息與美食饗宴的阿美族風味餐廳。基於長輩的期望與祖靈的頻頻召喚，鄭氏夫婦放棄公職及創意設計的工作，接手長輩所經營的「紅瓦屋餐廳」。餐廳的紅色屋頂是最為顯著的地標，隨處可以見到以石頭、流木、廢棄的農村器具所建構出的自然純樸空間，還有許多原住民木雕與小裝飾品，散發出濃濃的原住民文化氣息，可以深深體會到店家的用心與細心。餐廳旁的濕地生態，包括了清香的荷花、多樣化的水生植物，還有終年不斷的天然湧泉，用來養殖台灣鯛魚，可說是獨天得厚的美味，以供應餐廳內使用，目前由第二代媳婦林鳳廷經營。阿美族是個母系社會，本身是藝術家的林鳳廷扛起家族餐飲事業後，並未忘情創作藝術，反而結合原住民傳統工藝，用漂流木、石頭、廢棄的農村器具，與家族兄弟姊妹DIY裝潢，為紅瓦屋營造初舒適的用餐空間，處...', '花蓮縣976光復鄉大全村大全街62巷16號', 976, '花蓮縣', '光復鄉', '886-3-8704601', '11:30 - 15:30、17:00 - 19:30週一、週四僅營業 11:30 - 15:30', '', '', '', '', '', '', '', '121.410260', '23.660810', 9, '', ''),
('C3_315080500H_000647', '德利豆乾專賣店', '八十年前，德利的開山祖師廖金火，從台北新店移居到花蓮，跟著親戚學做豆腐，清晨即起，忍受著高溫蒸氣做豆腐，在挑著豆腐沿街叫賣，他克勤克儉，期勉子孫&rdquo;德可潤身、利足健民&rdquo;的&rdquo;德利&rdquo;精神。德利的精神，一代接一代，從第二代的廖樹根，到第三代廖文雄，至今第四代廖堯宇，代代相傳，推陳出新，從豆腐、紫蘇梅、到白梅、調味豆乾、剝皮辣椒、一代傳一代，也體會到了&rdquo;做人要像豆腐，心要柔軟，作事要像滷豆干，滷的徹底，才會入味&rdquo;本著傳承的精神，將產品不斷推陳出新，並堅持品質，至今門市已有數十種產品，並希望能將其品牌推廣至各地，成為來花蓮必帶的產品之一。', '花蓮縣970花蓮市大禹街6號', 970, '花蓮縣', '花蓮市', '886-3-8338039', '9:00~10:00，周六營業到10:30', '', '', '', '', '', '', '', '121.609080', '23.976530', 9, '', ''),
('C3_315080500H_000648', '阿美麻糬', '民以食為天，雖然食品業可說是最古老的行業，但宗泰人卻開創出一片新的天地；在這過程中，始終秉持者「尊重傳統、努力創新」的原則，更奠定了今日輝煌成就的基礎。過去三十年來，余董事長從山產到海產的經營，從加工製造到批發銷售的歷程中，累積了許多經驗與心得。設立宗泰食品股份有限公司的時候，特別揭示︰「傳動文化食品，永無休止運轉」作為事業推展的方向；從此，宗泰人有了正確的努力方向，讓飲食不再只是滿足口腹之慾，更昇華為文化之基石。', '花蓮縣973吉安鄉南濱路一段258號', 973, '花蓮縣', '吉安鄉', '886-3-8420033', '07:30~18:00', '', '', '', '', '', '', '', '121.597480', '23.947670', 9, '', ''),
('C3_315080500H_000651', '東昇茶行', '東昇茶行早期以鳥龍及金宣為主要茶品，近年來積極研發新口味的茶葉，在今年世界紅茶比賽時，以自家研發、口味獨特的蜜香紅茶獲得世界冠軍的殊榮，有許多遊客特地來此品嘗紅茶及購買。東昇茶行為舞鶴地區無毒農業的示範茶行，無毒農業是指在栽種過程中不能使用農藥，因此在栽種過程常會遭遇茶被蟲咬，使茶葉不能採收，造成成本的增加，但在這偶然的機會，老闆娘將被蟲(小綠葉蟬)咬過的大葉烏龍茶葉做成紅茶，意外發現口感相當獨特，喝起來有天然蜜香的味道，因此稱蜜香紅茶。以往茶葉的產期大部份集中在春茶及冬茶，因為氣候較適合，茶葉口感較好，而小綠葉蟬的盛行是在夏天，因此現在夏季也成了蜜香紅茶的產期，一年四季都將是喝茶的好季節，且一般年輕人對紅茶接受度也較高，讓喝茶(老人茶)不再是老人的專利。蜜香紅茶有茶葉及茶包形式，沖泡方式除了以熱水沖泡之外，也可以冷泡法飲用，相當方便。老闆娘保留阿拉比卡的樹種，產出的阿...', '花蓮縣978瑞穗鄉舞鶴村13鄰256-1號', 978, '花蓮縣', '瑞穗鄉', '886-3-8871878', '08:00 - 19:00', '', '', '', '', '', '', '', '121.359880', '23.462840', 9, '', ''),
('C3_315080500H_000652', '綠精靈瑞穗鮮奶鍋', '大多數的人一定都知道，花蓮瑞穗最有名的就是好喝鮮奶，但如果在這裡還只喝著香純牛奶～那就有些遜色囉！瑞穗鄉不只有新鮮的牛奶，更運用特產鮮奶，製作出各式美味料理呢！位於瑞穗火車站附近的「綠精靈瑞穗鮮奶鍋」創立於2001年，店內最有名氣的鮮奶鍋，正是用純正的瑞穗鄉鮮奶調製而成， 而這份完美的好味道，不僅在於濃純的鮮奶湯頭， 就連烹煮在鮮奶中的肉片及蔬菜，都令人讚不絕口呢！', '花蓮縣978瑞穗鄉成功北路52號', 978, '花蓮縣', '瑞穗鄉', '886-3-8876306', '週一~週五 11:00–14:00， 17:00–21:00週六、周日 11:00–21:00', '', '', '', '', '', '', '', '121.377060', '23.499990', 9, '', ''),
('C3_315080500H_000653', '樹屋餐飲', '花蓮縣壽豐鄉池南社區(Ba-Naw部落)位於花蓮縣鯉魚潭風景區內，世居阿美族CiKasuan族群(南勢阿美族)，這棟以木頭及竹子搭建而成的餐廳，是一間由池南社區發展協會經營的社區餐廳，所料理的美味佳餚都是取自池南社區生產的無毒食材，由於物美價廉，材料新鮮，吸引許多外地觀光客或花蓮在地人到此用餐，也提供社區婦女就業機會。在如此低迷的情況下，部落展開自救行動，「樹屋」正是族人自救的成果展現。池南社區發展協會在九十四年六月，以「被遺忘的原始部落fanaw」，向勞委會申請多元就業方案經濟型計畫，族中長者以傳統造屋技巧豎立建築物的根基，作為推動社區工作的場域。對餐飲業毫無經驗的社區居民，在短短兩個月內，推出具有特色的原住民風味餐。在全員通力合作之下，創造了「樹屋」的經營奇蹟。', '花蓮縣974壽豐鄉池南村環潭南路2號', 974, '花蓮縣', '壽豐鄉', '886-9-55996777', '週一~週五 10:00~14:00、17:00~20:00週六~周日 10:00~20:00每週二公休', '', '', '', '', '', '', '', '121.508810', '23.922840', 9, '', '');
